import React, { useState } from 'react';
import './assets/ModalSeleccionRegistro.css';
import { useNavigate } from 'react-router-dom';
const ErrorMessage = ({ idEvento, onClose }) => {
  const Navigate = useNavigate();
  const handleGrupalClick = () => {
    // Llama a la función onClose para cerrar el modal
    onClose();
    window.location.href = `./registroParticipantes?${i}`;
  };
  const handleIndividualClick = () => {
    // Llama a la función onClose para cerrar el modal
    onClose();
    Navigate('/registroParticipantes');
  };
  return (
    <div className='modal-container'>
      <div className='modal-content error'>
      <span className="cerrar" onClick={onClose}>&times;</span>
       <h1 >Selecciona la accion que deseas</h1>
        <div className='boton-ok'>
          
        <button onClick={handleGrupalClick}>Registrar el nombre del equipo</button>
      </div>
      <div className='boton-ok'>
          <button onClick={handleIndividualClick}>Registrarse a un equipo</button>
          
        </div>
      </div>
    </div>
  );
};
//export default ErrorMessage;


const RegistroForm = () => {
  const [showErrorModal, setShowErrorModal] = useState(false);

  const closeErrorModal = () => {
    setShowErrorModal(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Lógica de registro exitoso (puede ser una llamada a una API, etc.)
    
    // Después de un registro exitoso, muestra el mensaje
    setShowErrorModal(true);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        

        <button type="submit">Registrar</button>
      </form>
      {showErrorModal && (
        <ErrorMessage message1="Quieres registrar un equipo? haz click aqui" message2="Quieres registrarte a un equipo? haz click aqui"onClose={closeErrorModal} />
      )}
          </div>
  );
};

export default RegistroForm;
