import React from 'react';
import '../components/assets/ModalCalendario.css'
    const ModalEvento = ({ evento,color, onClose }) => {
        return (
          <div className="modal" >
            <div className="modal-content" style={{ backgroundColor: color }}> {/* Agrega una clase para el estilo bohemio */}
              <span className="close" onClick={onClose}>&times;</span>
              <h2>{evento.nombreActividad}</h2> {/* Corrige el nombre de la propiedad */}
              <p><strong>Fecha de inicio:</strong> {evento.horaInicioActividad}</p>
              <p><strong>Hora de inicio:</strong> {evento.horaInicioActividad}</p>
              <p><strong>Hora de inicio:</strong> {evento.horaInicioActividad}</p>
              <p><strong>Modalidad:</strong> {evento.modalidad}</p>
              <p><strong>Ubicacion:</strong> {evento.ubicacionActividad}</p>
              {/* Agrega aqu√≠ cualquier otro dato que quieras mostrar */}
            </div>
          </div>
        );
      };
export default ModalEvento;
