<br></br>

        <label htmlFor="Descripcion">Descripción  </label>
        <textarea
          id="descripcion"
          name="descripcion"
          placeholder="Descripción del evento"
          rows="4"
          value={descripcion}
          onChange={handleDescripcionChange}
          
        ></textarea>

        <p className="error-message">{descripcionError}</p> {/* Nuevo */}


        

      
