import React, { useState } from 'react';
import axios from './api/conexionApi';
import './assets/registroParticipantes.css';
const evento=1;
const FormularioRegistroParticipantes = (idEvento) => {
  const [formData, setFormData] = useState({
    idPersona: '',
    nombrePersona: '',
    apellidoPersona: '',
    genero: '',
    telefonoPersona: ''
  });

  const [correoCorreo, setCorreoC] = useState('');

const handleChangeCorreo = (e) => {
  const { value } = e.target;
  setCorreoC(value);
};

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };
 
 
  
  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('./storePersona', formData)
      .then((a) => {
        
        console.log('Datos guardados correctamente', formData.idPersona);

        const correo = {
            correoC: correoCorreo,   
            estadoNotificacion: true,
            idPersona: formData.idPersona
          };
          console.log('Datos editados', correo.idPersona);

          axios.post('./correos', correo)
          .then((b)=>{

          console.log('Datos de correo guardados correctamente', correo.correoC);

          })
          .catch((error) => {
       
            console.error('Error al guardar correo ', error);
          });


        
          
      })
      .catch((error) => {
       
        console.error('Error al guardar los datos', error);
      });
  };

  return (
    <div>
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="idPersona">ID de Persona:</label>
        <input
          type="text"
          id="idPersona"
          name="idPersona"
          value={formData.idPersona}
          onChange={handleChange}          
        />
      </div>
      
      <div>
        <label htmlFor="nombrePersona">Nombre:</label>
        <input
          type="text"
          id="nombrePersona"
          name="nombrePersona"
          value={formData.nombrePersona}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="apellidoPersona">Apellido:</label>
        <input
          type="text"
          id="apellidoPersona"
          name="apellidoPersona"
          value={formData.apellidoPersona}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="genero">Género:</label>
        <input
          type="text"
          id="genero"
          name="genero"
          value={formData.genero}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="telefonoPersona">Teléfono:</label>
        <input
          type="text"
          id="telefonoPersona"
          name="telefonoPersona"
          value={formData.telefonoPersona}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="correoC">correo:</label>
        <input
          type="text"
         
          value={correoCorreo}
          onChange={handleChangeCorreo}
        />
      </div>
      


 
      
      <button type="submit">Guardar</button>
    </form>
    
    
      </div>


  );


}
export default FormularioRegistroParticipantes;
