import React, { useState } from 'react';
import axios from 'axios';
import './assets/configurarEvento.css';

const ConfiguracionEvento = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);

  const options = [
    'Opción 1',
    'Opción 2',
    'Opción 3',
    'Opción 4',
    'Opción 5',
    'Opción 6',
    'Opción 7',
    'Opción 8',
  ];

  const handleOptionClick = (option) => {
    const isSelected = selectedOptions.includes(option);
    if (isSelected) {
      const filteredOptions = selectedOptions.filter((item) => item !== option);
      setSelectedOptions(filteredOptions);
    } else {
      setSelectedOptions([...selectedOptions, option]);
    }
  };

  return (
    <div className="form-container">
      <h2>Titulo del Formulario</h2>
      <div className="options-container">
        <div className="left-column">
          {options.slice(0, 4).map((option, index) => (
            <div key={index} onClick={() => handleOptionClick(option)} className={selectedOptions.includes(option) ? 'selected' : ''}>
              {option}
            </div>
          ))}
        </div>
        <div className="right-column">
          {options.slice(4).map((option, index) => (
            <div key={index + 4} onClick={() => handleOptionClick(option)} className={selectedOptions.includes(option) ? 'selected' : ''}>
              {option}
            </div>
          ))}
        </div>
      </div>
      <div className="buttons-container">
        <button className="cancel-button">Cancelar</button>
        <button className="next-button">Siguiente</button>
      </div>
    </div>
  );
};

export default ConfiguracionEvento;
