import React, { useState } from 'react';
import axios from 'axios';
import '../components/assets/FormCrearEvento.css';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


const EventForm = () => {
 // const [titulo, setTitulo] = useState('');
  const [nombre, setNombre] = useState('');
  const [registroExitoso, setRegistroExitoso] = useState(false);
  const [descripcion, setDescripcion] = useState('');


  const handleNombreChange = (e) => {
    setNombre(e.target.value);
  };


  const handleDescripcionChange = (e) => {
    setDescripcion(e.target.value);
  };

  const handleCancel = () => {
    // Redirigir a la página principal al presionar "Cancelar"
    window.location.href = 'http://localhost:3000/home';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();


    if (!/^[a-zA-Z\s]*$/.test(nombre)) {
      toast.error('El campo de nombre no debe contener números ni caracteres especiales', {
        position: 'top-right',
        autoClose: 3000,
      });
      alert('El campo de nombre no debe contener números ni caracteres especiales.');
      return;
    }

    if (nombre.length > 25) {
      //toast.error('El campo de nombre no debe exceder los 25 caracteres', {
       // position: 'top-right',
       // autoClose: 3000,
     // });
      alert('El campo de nombre no debe exceder los 25 caracteres.');
      return;
    }

    const formData = new FormData();
    //formData.append('titulo', titulo);
    formData.append('nombreTipoEvento', nombre);
    formData.append('descripcionTipoEvento', descripcion);


    try {
      // Realizar la solicitud POST con formData usando axios
      //await axios.post('http://localhost:8000/api/eventos', formData);
      await axios.post('http://localhost:8000/api/CrearTipoEvento', formData);

      /* toast.success('Evento creado con éxito', {
        position: 'top-right',
        autoClose: 3000,
      }); */
      setRegistroExitoso(true); // Mostrar el mensaje de registro exitoso

      console.log('Evento creado con éxito');
    } catch (error) {
      console.error('Error al crear evento', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="tweet-composer">

      <div className="composer-form">
        <label htmlFor="nombre">Nombre</label>
        <input
          type="text"
          id="nombre"
          name="nombre"
          placeholder="Nombre del tipo de evento"
          value={nombre}
          onChange={handleNombreChange}
          required
        />

        <label htmlFor="Descripcion">Descripción</label>
        <textarea
          id="descripcion"
          name="descripcion"
          placeholder="Descripción del evento"
          rows="4"
          value={descripcion}
          onChange={handleDescripcionChange}
          required
         ></textarea>


<br>
</br>

       <div className="container">
         {/* <button className="tweet-button" type="submit">
            Cancelar
          </button> */}
         <button className="tweet-button" type="button" onClick={handleCancel}>
            Cancelar
          </button>

          <button className="tweet-button" type="submit" onClick={handleSubmit}>
            Crear
          </button>
        </div> 


        {registroExitoso && (
          <div className="mensaje-exitoso">
            ¡Registro exitoso! El evento se ha creado.
          </div>
        )}
      </div>
    </form>
  );
};

export default EventForm;
