        if (value.length > 30) {
        
        setNombreError(errorTreintaCaracteres);
        } else if (newValueAlpha !== value) {
          setNombreError( errorCaracteresNoPermitidos);
         
        } else {
         
          setNombreError('');
        }
  
        setFormData({
          ...formData,
          [name]: newValueAlpha,
        });
        break;
          case 'apellidoPersona':
            // Permitir solo caracteres alfabéticos y espacios, con un límite de 30 caracteres
            const newValueAlpha2 = value.replace(/[^a-zA-Z\s]/g, '').substring(0, 30);
            
            if (value.length > 30) {
            //  setErrorMessage(`El campo ${name} no puede superar los 30 caracteres`);
            setApellidoError(errorTreintaCaracteres);
            } else if (newValueAlpha2 !== value) {
              setApellidoError(errorCaracteresNoPermitidos);
             // setErrorMessage(`Caracteres no permitidos en ${name}`);
            } else {
              //setErrorMessage('');
              setApellidoError('');
            }
      
            setFormData({
              ...formData,
              [name]: newValueAlpha2,
            });
            break;

      case 'idPersona':
        const newValueID = value.replace(/[^\d]/g, '').substring(0, 8);
        
    if (newValueID !== value) {
      setIdPersonaError(errorSoloNumeros);
    } else if (value.length > 8) {
      setIdPersonaError(errorOchoCaracteres);
      
    } else if (value.length < 7) {
      setIdPersonaError(errorSieteCaracteres);
    } else {
      setIdPersonaError('');
    }

    setFormData({
      ...formData,
      [name]: newValueID,
    });
    break;
              

      case 'equipo':
    if (value !== 'Selecciona un equipo' ) {
      console.log("genroooo",value)
      setGeneroError('');
      setFormData({
        ...formData,
        [name]: value,
      });
    } else {
      setGeneroError(errorSeleccioneUnaOpcion);
    }
   break;

      case 'telefonoPersona':
        const newValueTel = value.replace(/[^\d]/g, '').substring(0, 8);
        if(value.length<7 ){
          if(newValueTel!==value){
              setTelefonoError(errorSoloNumeros);
          }else{
          setTelefonoError(errorSieteCaracteres);
          }
          }else{
              if(value.length>8){
                  setTelefonoError(errorOchoCaracteres);
              }else{                    
                  setTelefonoError('');
              }
          }

        setFormData({
          ...formData,
          [name]: newValueTel,
        });
        break;

      default:
        setFormData({
          ...formData,
          [name]: value,
        });
    }
};
 
  const handleSubmit = (e) => {
    //ERRORES
    if (formData.nombrePersona.length < 3 || formData.nombrePersona.length > 30) {
      setNombreError("Por favor, ingrese un nombre válido (entre 3 y 30 caracteres).");
      return;
    } else {
      setNombreError("");
    }

    if (formData.apellidoPersona.length < 3 || formData.apellidoPersona.length > 30) {
      setApellidoError("Por favor, ingrese un apellido válido (entre 3 y 30 caracteres).");
      return;
    } else {
      setApellidoError("");
    }
    
    if (formData.idPersona.length < 7 || formData.idPersona.length > 8) {
      setIdPersonaError("Por favor, ingrese CI válido (entre 7 y 8 caracteres).");
      return;
    } else {
      setIdPersonaError("");
    }
    if (correoData.correoC.length < 1 ) {
      setCorreoError("Por favor, ingrese un correo electronico");
      return;
    } else {
      setCorreoError("");
    }
//ERRORES
    e.preventDefault();
    axios.post('./storePersona', formData)
      .then((a) => {
        
       console.log('Datos guardados correctamente', formData.idPersona);
       const formCorreo = {
        correoC: correoData.correoC,   
        estadoNotificacion: true,
        idEvento: formData.idPersona
       };
       axios.post('./correos', formCorreo)
          .then((c)=>{
            console.log('correo guardado', correoData.correoC);
            const formParticipante = {
              idParticipante: formData.idPersona,   
              idEvento: evento,
              idEquipo: null
            };

            console.log('Datos editados', formParticipante.idParticipante);

              axios.post('./storeParticipante', formParticipante)
              .then((b)=>{
                    console.log('Datos de participante guardados correctamente', formParticipante.idParticipante);
                    
                    setFormData({
                      idPersona: '',
                      nombrePersona: '',
                      apellidoPersona: '',
                      genero: '',
                      telefonoPersona: ''
                    });
                    setCorreoData({
                      correoC:''
                    });
                    
              })
              .catch((error) => {          
                console.error('Error al guardar participante ', error);
                
              });         
            })
           .catch((error) => {       
            console.error('Error al guardar los datos del correo', error);
            });
      //alert("Datos guardados exitosamente.");
      setRegistroExitoso(true);
    })
    .catch((error) => {       
      console.error('Error al guardar los datos de persona', error);
      setShowErrorModal(true);
      });
      
  }


  const [equipos, setEquipos] = useState([]);
  const [selectedEquipo, setSelectedEquipo] = useState('');

  useEffect(() => {
    const fetchEquipos = async () => {
      try {
        const response = await axios.get(`/equiposPorEvento/${evento}`);
        setEquipos(response.data.equipos);
      } catch (error) {
        console.error('Error al obtener equipos:', error);
      }
    };

    fetchEquipos();
  }, [evento]);


  return (
    <div className='contenedor-form '>
    <br/>
      <h1 className='centrar-titulo'>Formulario de Registro de Participante</h1>
      <br/>
    <form onSubmit={handleSubmit}>
            
      <div>
        <label class="subtitulo required"  htmlFor="nombrePersona">Nombre:</label>
        <input
          type="text"
          id="nombrePersona"
          name="nombrePersona"
          value={formData.nombrePersona}
          placeholder="Ingresa tu nombre"
          onChange={handleChange}
        />
         <p style={{ color: 'red' }}>{nombreError}</p>
      </div>
      <br/>
      <div>
        <label class="subtitulo required" htmlFor="apellidoPersona">Apellido:</label>
        <input
          type="text"
          id="apellidoPersona"
          name="apellidoPersona"
          value={formData.apellidoPersona}
          placeholder="Ingresa tus apellidos"
          onChange={handleChange}          
        />
         <p style={{ color: 'red' }}>{apellidoError}</p>
      </div>
      <br/>
      <div>
        <label class="subtitulo required" htmlFor="idPersona">CI: </label>
        <input
          type="text"
          id="idPersona"
          name="idPersona"
          value={formData.idPersona}
          placeholder="Ingresa tu numero de identificacion"
          onChange={handleChange}          
        />
         <p style={{ color: 'red' }}>{idPersonaError}</p>
      </div>
      <br/>
      <div>
      <label class="subtitulo required">Selecciona un equipo:</label>
      <select
        value={selectedEquipo}
        onChange={(e) => setSelectedEquipo(e.target.value)}
      >
        <option value="">Seleccione un equipo</option>
        {equipos.map((equipo) => (
          <option key={equipo.idEquipo} 
          id='equipo'
          name='equipo'
          value={equipo.idEquipo}>
            {equipo.nombreEquipo}
          </option>
          
        ))}
      </select>
      <p>{selectedEquipo}</p>
        <p style={{ color: 'red' }}>{generoError}</p>
      </div>
      <br/>
      <div>
        <label className='subtitulo' htmlFor="telefonoPersona">Teléfono:</label>
        <input
          type="text"
          id="telefonoPersona"
          name="telefonoPersona"
          value={formData.telefonoPersona}
          placeholder="60004533"
          onChange={handleChange}
        />
         <p style={{ color: 'red' }}>{telefonoError}</p>
      </div>
      <br/>
      <div>
        <label class="subtitulo required" htmlFor="correoC">Correo:</label>
        <input
          type="text"
          id="correoC"
          name="correoC"
          value={correoData.correoC}
          placeholder="example@gmail.com"
          onChange={handleChangeCorreo}
        />
         <p style={{ color: 'red' }}>{correoError}</p>
      </div>
      <br/>
       <div className='button-container-RP'>
          <button className="blue-button-RP" type="button" onClick={() => handleInicioClick()}>Salir</button>
          <button className="blue-button-RP" type="submit">Registrar</button>
       </div>
  </form>

      {registroExitoso && <SuccessMessage message="¡Registro exitoso!" />}
      {showErrorModal && (
        <ErrorMessage message="Ha ocurrido un error al realizar el registro, intentelo nuevamente" onClose={closeErrorModal} />
      )}
      {mostrarModalSalir && (
        <ModalSalir message="¿Quiere abandonar el registro?" onClose={handleCloseModalSalir} />
      )}
    </div>
  );
}
export default FormularioRegistroParticipantes;
