import React, { useState } from 'react';
import axios from 'axios';
import './assets/registroParticipantes.css';

const FormularioRegistro = () => {
  const [nombre, setNombre] = useState('');
  const [apellidos, setApellidos] = useState('');
  const [correo, setCorreo] = useState('');
  const [ci, setCI] = useState('');
  const [celular, setCelular] = useState('');
  const [equipo, setEquipo] = useState('');
  const [genero, setGenero] = useState('Masculino'); // Establece un valor predeterminado
  const [rol, setRol] = useState('Participante'); // Establece un valor predeterminado
  const [institucion, setInstitucion] = useState('');
  const [codigoSIS, setCodigoSIS] = useState('');
  const [semestre, setSemestre] = useState('1'); // Establece un valor predeterminado
  const [notificaciones, setNotificaciones] = useState(false);

  const handleInputChange = (e, stateSetter) => {
    stateSetter(e.target.value);
  };

  const handleCheckboxChange = (e) => {
    setNotificaciones(e.target.checked);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (
      nombre.trim() === '' ||
      apellidos.trim() === '' ||
      correo.trim() === '' ||
      !/^[\w.-]+@[a-zA-Z\d.-]+\.[a-zA-Z]{2,4}$/i.test(correo) || // Validación de correo electrónico
      !/^\d{8}$/.test(celular) || // Validación de celular
      !/^\d{12}$/.test(ci) || // Validación de C.I.
      !/^\d{9}$/.test(codigoSIS) // Validación de Código SIS
    ) {
      alert('No se pueden ingresar los datos en este formato');
      return;
    }

    // Envía los datos del formulario a través de una solicitud AJAX
    try {
      await axios.post('http://localhost:8000/api/registro', {
        nombre: nombre,
        apellidos: apellidos,
        correo: correo,
        ci: ci,
        celular: celular,
        equipo: equipo,
        genero: genero,
        rol: rol,
        institucion: institucion,
        codigoSIS: codigoSIS,
        semestre: semestre,
        notificaciones: notificaciones ? 1 : 0,
      });
      alert('¡Datos ingresados exitosamente!');
    } catch (error) {
      if (error.response) {
        console.error('Respuesta del servidor con error:', error.response);
        console.error('Código de estado HTTP:', error.response.status);
      } else if (error.request) {
        console.error('No se recibió respuesta del servidor:', error.request);
      } else {
        console.error('Error durante la configuración de la solicitud:', error.message);
      }
    }
  };

  return (
    <form onSubmit={handleSubmit} className="tweet-composer">
      <div className="composer-form">
        <form id="registration-form" onSubmit={handleSubmit}>
          <h1>Datos Personales</h1>

          <label htmlFor="nombre">
            Nombre: <span style={{ color: 'red' }}>*</span>
          </label>
          <input
            type="text"
            id="nombre"
            name="nombre"
            placeholder="Nombre"
            value={nombre}
            onChange={(e) => handleInputChange(e, setNombre)}
            required
            maxLength="30" // Límite de 30 caracteres
            pattern="[^0-9]+" // No se permiten números en el nombre
          />

          <label htmlFor="apellidos">
            Apellidos: <span style={{ color: 'red' }}>*</span>
          </label>
          <input
            type="text"
            id="apellidos"
            name="apellidos"
            placeholder="Apellidos"
            value={apellidos}
            onChange={(e) => handleInputChange(e, setApellidos)}
            required
            maxLength="30" // Límite de 30 caracteres
            pattern="[^0-9]+" // No se permiten números en los apellidos
          />

          <label htmlFor="correo">
            Correo electrónico: <span style={{ color: 'red' }}>*</span>
          </label>
          <input
            type="email"
            id="correo"
            name="correo"
            placeholder="Correo electrónico"
            value={correo}
            onChange={(e) => handleInputChange(e, setCorreo)}
            required
          />

          <div className="split-row">
            <div className="split-column">
              <label htmlFor="ci">
                C.I.: <span>*</span>
              </label>
              <input
                type="text"
                id="ci"
                name="ci"
                placeholder="Carnet de identidad"
                value={ci}
                onChange={(e) => handleInputChange(e, setCI)}
                required
                maxLength="12" // Límite de 12 caracteres
                pattern="^\d{12}$" // 12 caracteres numéricos
              />

              <label htmlFor="celular">
                Celular: <span>*</span>
              </label>
              <input
                type="text"
                id="celular"
                name="celular"
                placeholder="Celular"
                value={celular}
                onChange={(e) => handleInputChange(e, setCelular)}
                required
                pattern="^\d{8}$" // 8 caracteres numéricos
              />
            </div>
            <div className="split-column">
              <fieldset className="role-fieldset">
                <legend>Género</legend>
                <div className="radio-group">
                  <label htmlFor="femenino">
                    <input
                      type="radio"
                      id="femenino"
                      name="genero"
                      value="Femenino"
                      checked={genero === 'Femenino'}
                      onChange={(e) => handleInputChange(e, setGenero)}
                    />
                    Femenino
                  </label>
                  <label htmlFor="masculino">
                    <input
                      type="radio"
                      id="masculino"
                      name="genero"
                      value="Masculino"
                      checked={genero === 'Masculino'}
                      onChange={(e) => handleInputChange(e, setGenero)}
                    />
                    Masculino
                  </label>
                  <label htmlFor="otro">
                    <input
                      type="radio"
                      id="otro"
                      name="genero"
                      value="Otro"
                      checked={genero === 'Otro'}
                      onChange={(e) => handleInputChange(e, setGenero)}
                    />
                    Otro
                  </label>
                </div>
              </fieldset>
            </div>
          </div>

          <h2>Información Adicional</h2>
          <label htmlFor="equipo">Nombre del Equipo:</label>
          <input
            type="text"
            id="equipo"
            name="equipo"
            placeholder="Nombre del equipo"
            value={equipo}
            onChange={(e) => handleInputChange(e, setEquipo)}
            maxLength="50" // Límite de 50 caracteres
          />

          <label htmlFor="rol">Rol en el Evento:</label>
          <select
            id="rol"
            name="rol"
            value={rol}
            onChange={(e) => handleInputChange(e, setRol)}
          >
            <option value="Participante">Participante</option>
            <option value="Voluntario">Voluntario</option>
            <option value="Organizador">Organizador</option>
          </select>

          <label htmlFor="institucion">Institución:</label>
          <input
            type="text"
            id="institucion"
            name="institucion"
            placeholder="Institución"
            value={institucion}
            onChange={(e) => handleInputChange(e, setInstitucion)}
            maxLength="100" // Límite de 100 caracteres
          />

          <label htmlFor="codigoSIS">Código SIS:</label>
          <input
            type="text"
            id="codigoSIS"
            name="codigoSIS"
            placeholder="Código SIS"
            value={codigoSIS}
            onChange={(e) => handleInputChange(e, setCodigoSIS)}
            maxLength="9" // Límite de 9 caracteres
            pattern="^\d{9}$" // 9 caracteres numéricos
          />

          <label htmlFor="semestre">Semestre:</label>
          <select
            id="semestre"
            name="semestre"
            value={semestre}
            onChange={(e) => handleInputChange(e, setSemestre)}
          >
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
            <option value="6">6</option>
            <option value="7">7</option>
            <option value="8">8</option>
          </select>

          <label htmlFor="notificaciones">Recibir Notificaciones:</label>
          <input
            type="checkbox"
            id="notificaciones"
            name="notificaciones"
            checked={notificaciones}
            onChange={handleCheckboxChange}
          />

          <div className="button-container">
            <button type="submit">Registrar</button>
          </div>
        </form>
      </div>
    </form>
  );
};

export default FormularioRegistro;
