import React, { useState } from 'react';
import './assets/ModalRegistroParticipantes.css';

// Importa la función para enviar el correo
import enviarCorreo from './utils/EnviarCorreo'; // Ajusta la ruta según sea necesario

const Modal = ({ isOpen, closeModal, handleYes, handleNo, datos }) => {
  const [codigo, setCodigo] = useState('');
  const [codigoEnviado, setCodigoEnviado] = useState(false);
  const [errorCodigo, setErrorCodigo] = useState('');

  // Función para enviar el código al correo
  const enviarCodigo = async () => {

    const generarCodigo = () => {
        // Genera un número entre 0 y 99999, luego convierte a cadena
        const codigo = Math.floor(Math.random() * 100000).toString();
      setCodigo(codigo);
      };      

     // Genera tu código aquí
    try {
      await enviarCorreo(datos.correo, 'Tu Código de Verificación', `Tu código es: ${codigo}`);
      setCodigoEnviado(true);
    } catch (error) {
      console.error('Error al enviar el correo', error);
    }
  };

  // Función para manejar la verificación del código
  const verificarCodigo = () => {
    if (codigo === '123456') { // Aquí debes verificar contra el código real que enviaste
      handleYes();
      closeModal();
    } else {
      setErrorCodigo('El código ingresado no es correcto.');
    }
  };

  if (!isOpen) {
    return null;
  }

  return (
    <div className='modal-container'>
      <div className='modal-content'>
        <h2>¿Ud. ya se ha registrado a un evento ICPC antes?</h2>
        <strong> Este es su correo: {datos.correo}</strong>
        {!codigoEnviado ? (
          <>
            <button className='boton-ok' onClick={enviarCodigo}>Enviar Código</button>
            <button className='boton-ok' onClick={handleNo}>No</button>
          </>
        ) : (
          <>
            <input type="text" placeholder="Ingrese el código" value={codigo} onChange={(e) => setCodigo(e.target.value)} />
            <button className='boton-ok' onClick={verificarCodigo}>Verificar Código</button>
          </>
        )}
        {errorCodigo && <p className="error">{errorCodigo}</p>}
        <span className="cerrar" onClick={closeModal}>&times;</span>
      </div>
    </div>
  );
};

export default Modal;
