import React, { useState } from 'react';
import axios from 'axios';
import './formulario.css';

const EventForm = () => {
  const [tituloEvento, setTituloEvento] = useState('');
  const [fechaInicioEvento, setFechaInicioEvento] = useState('');
  const [fechaFinEvento, setFechaFinEvento] = useState('');
  const [descripcionEvento, setDescripcionEvento] = useState('');
  const [aficheEvento, setAficheEvento] = useState(null);
  const [estadoEvento, setEstadoEvento] = useState('');
  const [horaInicioEvento, setHoraInicioEvento] = useState('');
  const [requisitosEvento, setRequisitosEvento] = useState('');
  const [idAdministrador, setIdAdministrador] = useState('');

  const handleTituloEventoChange = (e) => setTituloEvento(e.target.value);
  const handleFechaInicioEventoChange = (e) => setFechaInicioEvento(e.target.value);
  const handleFechaFinEventoChange = (e) => setFechaFinEvento(e.target.value);
  const handleDescripcionEventoChange = (e) => setDescripcionEvento(e.target.value);
  const handleAficheEventoChange = (e) => setAficheEvento(e.target.files[0]);
  const handleEstadoEventoChange = (e) => setEstadoEvento(e.target.value);
  const handleHoraInicioEventoChange = (e) => setHoraInicioEvento(e.target.value);
  const handleRequisitosEventoChange = (e) => setRequisitosEvento(e.target.value);
  const handleIdAdministradorChange = (e) => setIdAdministrador(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('tituloEvento', tituloEvento);
    formData.append('fechaInicioEvento', fechaInicioEvento);
    formData.append('fechaFinEvento', fechaFinEvento);
    formData.append('descripcionEvento', descripcionEvento);
    formData.append('aficheEvento', aficheEvento);
    formData.append('estadoEvento', estadoEvento);
    formData.append('horaInicioEvento', horaInicioEvento);
    formData.append('requisitosEvento', requisitosEvento);
    formData.append('idAdministrador', idAdministrador);

    try {
      await axios.post('http://localhost:8000/api/evento', formData);
      console.log('Evento creado con Ã©xito');
    } catch (error) {
      console.error('Error al crear evento', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit} className="evento-form">
        <div className="form-group">
          <label htmlFor="tituloEvento">TÃ­tulo del Evento</label>
          <input
            type="text"
            id="tituloEvento"
            name="tituloEvento"
            value={tituloEvento}
            onChange={handleTituloEventoChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="fechaInicioEvento">Fecha Inicio</label>
          <input
            type="date"
            id="fechaInicioEvento"
            name="fechaInicioEvento"
            value={fechaInicioEvento}
            onChange={handleFechaInicioEventoChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="fechaFinEvento">Fecha Fin</label>
          <input
            type="date"
            id="fechaFinEvento"
            name="fechaFinEvento"
            value={fechaFinEvento}
            onChange={handleFechaFinEventoChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="descripcionEvento">DescripciÃ³n</label>
          <textarea
            id="descripcionEvento"
            name="descripcionEvento"
            rows="4"
            value={descripcionEvento}
            onChange={handleDescripcionEventoChange}
            required
          ></textarea>
        </div>

        <div className="form-group">
          <label htmlFor="aficheEvento">Afiche del Evento</label>
          <input
            type="file"
            id="aficheEvento"
            name="aficheEvento"
            accept="image/*"
            onChange={handleAficheEventoChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="estadoEvento">Estado del Evento</label>
          <select
            id="estadoEvento"
            name="estadoEvento"
            value={estadoEvento}
            onChange={handleEstadoEventoChange}
            required
          >
            <option value="Activo">Activo</option>
            <option value="Inactivo">Inactivo</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="horaInicioEvento">Hora de Inicio</label>
          <input
            type="time"
            id="horaInicioEvento"
            name="horaInicioEvento"
            value={horaInicioEvento}
            onChange={handleHoraInicioEventoChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="requisitosEvento">Requisitos del Evento</label>
          <textarea
            id="requisitosEvento"
            name="requisitosEvento"
            rows="4"
            value={requisitosEvento}
            onChange={handleRequisitosEventoChange}
            required
          ></textarea>
        </div>

        <div className="form-group">
          <label htmlFor="idAdministrador">ID del Administrador</label>
          <input
            type="text"
            id="idAdministrador"
            name="idAdministrador"
            value={idAdministrador}
            onChange={handleIdAdministradorChange}
            required
          />
        </div>

        <button type="submit">Guardar</button>
      </form>
    </div>
  );
};

export default EventForm;
