// Formulario.jsx
import React, { useState } from 'react';
import axios from '../components/api/conexionApi';

const Formulario = () => {
  const [nombrePersona, setNombrePersona] = useState('');
  const [apellidoPersona, setApellidoPersona] = useState('');
  const [genero, setGenero] = useState('');
  const [telefonoPersona, setTelefonoPersona] = useState('');
  const [correoC, setCorreoC] = useState('');
  const [estadoNotificacion, setEstadoNotificacion] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Enviar datos de persona
      const personaData = { nombrePersona, apellidoPersona, genero, telefonoPersona };
      const personaResponse = await axios.post('./storePersona', personaData);

      // Enviar datos de correo con el ID de persona asociado
      const correoData = { correoC, estadoNotificacion, idPersona: personaResponse.data.idPersona };
      await axios.post('URL_API_CORREO', correoData);

      // Limpiar el formulario después de enviar los datos
      setNombrePersona('');
      setApellidoPersona('');
      setGenero('');
      setTelefonoPersona('');
      setCorreoC('');
      setEstadoNotificacion('');

      console.log('Datos enviados con éxito');
    } catch (error) {
      console.error('Error al enviar datos:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Inputs para datos de persona */}
      <label>Nombre:</label>
      <input type="text" value={nombrePersona} onChange={(e) => setNombrePersona(e.target.value)} />

      <label>Apellido:</label>
      <input type="text" value={apellidoPersona} onChange={(e) => setApellidoPersona(e.target.value)} />

      <label>Género:</label>
      <input type="text" value={genero} onChange={(e) => setGenero(e.target.value)} />

      <label>Teléfono:</label>
      <input type="text" value={telefonoPersona} onChange={(e) => setTelefonoPersona(e.target.value)} />

      {/* Inputs para datos de correo */}
      <label>Correo:</label>
      <input type="text" value={correoC} onChange={(e) => setCorreoC(e.target.value)} />

      <label>Estado de notificación:</label>
      <input type="text" value={estadoNotificacion} onChange={(e) => setEstadoNotificacion(e.target.value)} />

      <button type="submit">Enviar</button>
    </form>
  );
};

export default Formulario;
