const EquipoFormulario = () => {
  //para equipo---------------------------------------------------------------------------------
  const [equipoData, setEquipoData] = useState({
    nombreEquipo: '',
    descripcionEquipo: '',
    idEntrenador: '',
  });

  //handle equipo
  const handleEquipoChange = (e) => {
    const { name, value } = e.target;
    setEquipoData((equipoData) => ({ ...equipoData, [name]: value }));
    
  };
//submit equipo 
const handleSubmitEquipo = (e) => {
  e.preventDefault();
  //guardar entrenador ---------------------------
  axios.post('./storeEntrenador', entrenadorData)
