    }
   break;

      case 'telefonoPersona':
         const telefonoRegex = value.replace(/[^\d]/g, '').substring(0, 8);
       
        if (value.length === 8) {
          if(value.length===8){
            setTelefonoError ("");
           }
        } else if (telefonoRegex !== value) {
          setTelefonoError (errorSoloNumeros);        
        } else if (value.length < 8) {
          setTelefonoError (errorMinOchoCaracteres);
        } else {
          setTelefonoError ("");
        }
           setFormData({
          ...formData,
          [name]: telefonoRegex,
           })
           
        break;

      default:
        setFormData({
          ...formData,
          [name]: value,
        });
    }
};
 
