import './assets/FormularioRegistrarEquipo.css'
import axios from "../components/api/conexionApi";
import { useEffect, useState } from 'react';

const EquipoFormulario = () => {
  //para equipo---------------------------------------------------------------------------------
  const [equipoData, setEquipoData] = useState({
    nombreEquipo: '',
    descripcionEquipo: '',
    idEntrenador: '',
  });

  //handle equipo
  const handleEquipoChange = (e) => {
    const { name, value } = e.target;
    setEquipoData((prevData) => ({ ...prevData, [name]: value }));
  };
//submit equipo 
  const handleSubmitEquipo = (e) => {
    e.preventDefault();
    //guardar entrenador ---------------------------
    axios.post('./storeEntrenador', entrenadorData)
      .then((a) => {
    //guardar EQUIPO
        axios.post('./storeEquipo', equipoData)
       .then((a) => {

        })
       .catch((error) => {          
       console.error('Error al guardar equipo ', error);      
        }); 


      })
      .catch((error) => {          
        console.error('Error al guardar entrenaodr ', error);        
      });         
    };
      
 

//para entrenador ---------------------------------------------------------------------------------
  const [entrenadorData, setEntrenadorData] = useState({
    nombreEntrenador: '',
    apellidoEntrenador: '',
    correoEntrenador: '',
  });
  const handleEntrenadorChange = (e) => {
    const { name, value } = e.target;
    setEntrenadorData((prevData) => ({ ...prevData, [name]: value }));
  };
  return (
    <form onSubmit={handleSubmitEquipo}>
      <label>
        Nombre del Equipo:
        <input
          type="text"
          name="nombreEquipo"
          value={equipoData.nombreEquipo}
          onChange={handleEquipoChange}
        />
      </label>
      <br />
      <label>
        Descripci√≥n del Equipo:
        <textarea
          name="descripcionEquipo"
          value={equipoData.descripcionEquipo}
          onChange={handleEquipoChange}
        />
      </label>
      <br />
      <label>
        ID del Entrenador:
        <input
          type="text"
          name="idEntrenador"
          value={equipoData.idEntrenador}
          onChange={handleEquipoChange}
        />
      </label>
      <br />
      <form onSubmit={handleSubmitEntrenador}>
      <label>
        Nombre del Entrenador:
        <input
          type="text"
          name="nombreEntrenador"
          value={entrenadorData.nombreEntrenador}
          onChange={handleEntrenadorChange}
        />
      </label>
      <br />
      <label>
        Apellido del Entrenador:
        <input
          type="text"
          name="apellidoEntrenador"
          value={entrenadorData.apellidoEntrenador}
          onChange={handleEntrenadorChange}
        />
      </label>
      <br />
      <label>
        Correo del Entrenador:
        <input
          type="text"
          name="correoEntrenador"
          value={entrenadorData.correoEntrenador}
          onChange={handleEntrenadorChange}
        />
      </label>
      <br />
      <button type="submit">Crear Entrenador</button>
    </form>
      <button type="submit">Crear Equipo</button>
    </form>
  );
};
export default EquipoFormulario;
