import React, { useState } from 'react';
import styles from './assets/ModalPersonaEncontrada.module.css'; // Asegúrate de que la ruta y el nombre del archivo sean correctos

import enviarCorreo from './utils/EnviarCorreo'; // Verifica la ruta

const Modal = ({ isOpen, closeModal, handleYes, handleNo, datos }) => {
    const [codigo, setCodigo] = useState('');
    const [codigoEnviado, setCodigoEnviado] = useState(false);
    const [errorCodigo, setErrorCodigo] = useState('');
    const [codigoIngresado, setCodigoIngresado] = useState('');

    const generarCodigo = () => {
        const codigo = Math.floor(Math.random() * 100000).toString();
        return codigo.padStart(5, '0');
    };
  
    const enviarCodigo = async () => {
        const codigoGenerado = generarCodigo();
        setCodigo(codigoGenerado);
    
        try {
            await enviarCorreo(datos.correo, 'Tu Código de Verificación', `Tu código es: ${codigoGenerado}`);
            setCodigoEnviado(true);
        } catch (error) {
            console.error('Error al enviar el correo', error);
        }
    };

    const verificarCodigo = () => {
        if (codigo === codigoIngresado) {
            handleYes();
            closeModal();
        } else {
            setErrorCodigo('El código ingresado no es correcto.');
        }
    };

    if (!isOpen) {
        return null;
    }

    return (
        <div className={styles.modalContainerMPE}>
            <div className={styles.modalContentMPE}>
                {/* Contenido del Modal */}
                {/* ... */}
                {!codigoEnviado ? (
                    <>
                        <button className={styles.botonOk} onClick={enviarCodigo}>Enviar código</button>
                        <button className={styles.botonOk} onClick={handleNo}>No</button>
                    </>
                ) : (
                    <>
                        <input
                            className={styles.inputCodigo}
                            type="text"
                            placeholder="Ingrese el código"
                            value={codigoIngresado}
                            onChange={(e) => setCodigoIngresado(e.target.value)}
                        />
                        <button className={styles.botonOk} onClick={verificarCodigo}>Verificar Código</button>
                    </>
                )}
                {errorCodigo && <p className={styles.error}>{errorCodigo}</p>}
                <span className={styles.cerrar} onClick={closeModal}>&times;</span>
            </div>
        </div>
    );
};

export default Modal;
