import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import '../components/assets/MyCalendar.css'; // Archivo CSS personalizado


const CalendarioDesplegable = ({ fechaInicio, fechaFin }) => {
 
    const minDate = new Date(fechaInicio);
    const maxDate = new Date(fechaFin);
  
    const tileContent = ({ date, view }) => {
      if (view === 'month' && date >= minDate && date <= maxDate) {
        return <div className="highlighted-date"> </div>;
      }
    };
  
    return (
      <div>
        <h1>Calendario</h1>
        <Calendar
          minDate={minDate}
          maxDate={maxDate}
          tileContent={tileContent}
          view={'month'} // Muestra inicialmente el mes
          activeStartDate={minDate} // Establece la fecha inicial
          activeEndDate={maxDate} // Establece la fecha final
        />
      </div>
    );
  };

export default CalendarioDesplegable;
