import React, { useState, useEffect } from 'react';
import './assets/HomeVistaEvento.css';
import axios from '../components/api/conexionApi';
import useFetchActividades from '../components/'

const nombreCronograma="Cronograma general";
const HomeVistaEvento = () => {
  const [actividades, setActividades] = useState([]);



  const [eventos, setEventos] = useState([]);

  useEffect(() => {
    const fetchEventos = async () => {
      try {
        const response = await axios.get('./index');
        setEventos(response.data.eventos);
      } catch (error) {
        console.error('Error al obtener eventos:', error);
      }
    };

    fetchEventos();
  }, []);

  const handleRegistrarseClick = async (idEvento) => {
    window.location.href = `./VerInformacionEvento?id=${idEvento}`;
  };

  return (
    <div className="cuerpo">
      <h3 className="titulo_home">Eventos en Curso</h3>
      <hr />

      <div className="eventos-container">
      {eventos.map(evento => {
          // Llama a la función useFetchActividades para obtener solo los datos de activity
          const activity = useFetchActividades(evento.idEvento);

          return (
        
          <div key={evento.idEvento} className="evento-rectangulo">
              
            <img src={evento.aficheEvento} alt={`Evento ${evento.idEvento}`} />
            
            <div className="evento-info">
              <h4>{evento.tituloEvento}</h4>
              <p>Fecha de inicio: {activity.fechaInicio}</p>
              <p>Fecha de fin: {activity.fechaFin}</p>
              {activity.descripcionActividad && <p>Descripción: {activity.descripcionActividad}</p>}
              <p>Estado: {evento.estadoEvento === 1 ? 'Activo' : 'Inactivo'}</p>
              <button className="masinfo" onClick={() => handleRegistrarseClick(evento.idEvento)}>
                Más información
              </button>
            </div>
          </div>
          
        )})};
      </div>
    </div>
  );
};

export default HomeVistaEvento;
