    //----------------------------------------------------------------------------------------------
    const { nombreEquipo, descripcionEquipo, idEvento } = equipo;
  
  
  
    const [isSecondaryModalOpen, setSecondaryModalOpen] = useState(false);

    const handleSelfClose = () => {
        setSecondaryModalOpen(false);
      
    };
  
