import React, { useState, useEffect } from 'react';
import axios from './api/conexionApi';

const EditarEvento = ({ eventoId }) => {
  const [evento, setEvento] = useState({
    tituloEvento: '',
    participacion: '',
    numParticipantes: 0,
    numEntrenadores: 0,
    descripcionEvento: '',
    estadoEvento: '',
    aficheEvento: '',
    idTipoEvento: 0,
    idAdministrador: 0,
  });

  useEffect(() => {
    // Hacer una solicitud para obtener la información del evento por su ID
    axios.get(`./eventos/${eventoId.eventoId}`)
      .then(response => {
        setEvento(response.data.evento);
      })
      .catch(error => {
        console.error('Error al obtener la información del evento', error);
      });
  }, [eventoId.eventoId]);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setEvento(prevEvento => ({
      ...prevEvento,
      [name]: value,
    }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    
    // Hacer una solicitud para actualizar la información del evento
    axios.put(`./eventoActualizar/${eventoId.eventoId}`, evento)
      .then(response => {
        console.log('Evento actualizado con éxito', response.data.evento);
        // Puedes redirigir al usuario a la página del evento o realizar otras acciones después de la actualización
      })
      .catch(error => {
        console.error('Error al actualizar el evento', error);
      });
  };

  return (
    <div>
      <h2>Editar Evento</h2>
      <form onSubmit={handleSubmit}>
        {/* Campos de formulario para editar la información del evento */}
        <label>Título del Evento:
          <input type="text" 
          name="tituloEvento" 
          value={evento.tituloEvento} 
          onChange={handleInputChange} />
        </label>
        {/* Otros campos... */}
        <button type="submit">Guardar Cambios</button>
      </form>
    </div>
  );
};

export default EditarEvento;
