import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import '../components/assets/MyCalendar.css';

const localizer = momentLocalizer(moment);

const MiCalendario = ({ fechaInicio, fechaFin ,eventos}) => {
    
//const eventosConColores = asignarColoresAleatorios(eventos);

const [eventosConColores, setEventosConColores] = useState([]);

  useEffect(() => {
    const eventosColoreados = asignarColoresAleatorios(eventos);
      setEventosConColores(eventosColoreados);    
      console.log("evento con color es ",eventosColoreados)
  }, [eventos]);

  

  const customDayPropGetter = (date) => {
    const customStyle = {};
   // const fechaEspecifica = fechaInicio; 
   console.log("fecha date es ",date.getDate())
   console.log(typeof date)
    console.log("fecha inicio es ",fechaInicio.getDate())
    console.log(typeof fechaInicio)
   // console.log("fecha especifica es ",fechaEspecifica)
   // if (moment(date).isSame(fechaEspecifica)) {
    if (date.getDate()===fechaInicio.getDate() && 
    date.getMonth()===fechaInicio.getMonth() &&
    date.getFullYear()===fechaInicio.getFullYear()) {
        console.log("la fecha y date son iguales ")
      customStyle.backgroundColor = "#37c7e0"; // Color de fondo rosa
    }

    return {
      style: customStyle,
    };
  };

  const dayStyleGetter = (date) => {
        const hasEvents = eventosConColores.some(evento => { 
            console.log("fecha start es ",evento.fechaInicioActividad)   
            console.log("fecha end es ",evento.fechaFinActividad)     
       // const eventStartDate =new Date(evento.start);        
       // const eventEndDate =new Date(evento.end);
       // console.log("date  start es ",eventStartDate) 
       // console.log("date  end es ",eventEndDate) 
        console.log("date ",date) 
        return date >= eventStartDate && date <= eventEndDate;
        
      });

    return hasEvents ? { style: { backgroundColor: '#37c7e0' } } : {};
  };

  return (
    <div className="mi-contenedor">
      <Calendar
        localizer={localizer}
        events={eventosConColores}
        startAccessor="fechaInicioActividad"
        endAccessor="fechaFinActividad"
        className="mi-calendario"
        views={['month', 'agenda']}
        toolbar={true}
        
        eventPropGetter={(event, start, end, isSelected) => {
           return {
            style: {
              
             backgroundColor: event.color, // Usa el color asignado al evento
           },
          };
        }}
        //eventPropGetter={eventStyleGetter}
        dayPropGetter={(date) => {
          const customStyle = customDayPropGetter(date).style;
          const eventStyle = dayStyleGetter(date).style;
          return { style: { ...customStyle, ...eventStyle } };
        }}
        
      
        
        
        defaultDate={fechaInicio}
        
        defaultView="month"
        
        //components={{
         //   event: EventoPersonalizado // Usa un componente personalizado para mostrar los eventos
      //    }}
      />
     <div>
     
     </div>
    </div>
  );
};

const asignarColoresAleatorios = (eventos) => {
    return eventos.map(evento => ({
      ...evento,
      color: getRandomColor(),
    }));
  };
const getRandomColor = () => {
    const hue = Math.floor(Math.random() * 360); // Tonos de 0 a 360
  const pastelSaturation = Math.floor(Math.random() * 20) + 70; // SaturaciÃ³n entre 70 y 90 para pasteles
  const pastelLightness = Math.floor(Math.random() * 20) + 70; // Brillo entre 70 y 90 para pasteles

  return `hsl(${hue}, ${pastelSaturation}%, ${pastelLightness}%)`;

  };
  
const EventoPersonalizado = ({ event }) => (
    <div>
      <div style={{ fontWeight: 'bold' }}>{event.title}</div>
      <div>{moment(event.start).format('HH:mm')} - {moment(event.end).format('HH:mm')}</div>
    </div>
  );

export default MiCalendario;
