import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import '../components/assets/FormCrearEvento.css';
import Alert from'./Alert';
const EventForm = () => {

  
  
  const [tituloEvento, setTituloEvento] = useState('');
  const [horaEvento, setHoraEvento] = useState('00:00');
  const [fechaInicio, setFechaInicio] = useState('null');
  const [fechaFin, setFechaFin] = useState('null');
  const [ubicacion, setUbicacion] = useState("");
  const [idTipoEvento, setIdTipoEvento] = useState('-- seleccione --');
  const [modalidad, setModalidad] = useState('-- seleccione --');
  const [descripcion, setDescripcion] = useState('');
  

  
  const [tituloEventoError, setTituloEventoError] = useState(false);
  const [horaEventoError, setHoraEventoError] = useState(false);
  const [fechaInicioError, setFechaInicioError] = useState(false);
  const [fechaFinError, setFechaFinError] = useState(false);
  const [modalidadError, setModalidadError] = useState(false);
  const [idTipoEventoError, setIdTipoEventoError] = useState(false);
  const [ubicacionError, setUbicacionError] = useState(false);
  const [descripcionError, setDescripcionError] = useState(false);
  const [modalVisible, setModalVisible] = useState(false);
  const [Error, setError] = useState(flase);


  const handleButtonCancelarCE = () => {    window.location.href = '/Home';};


  const handleSubmit = async (e) => {
    e.preventDefault();
    

    
    try {
      
      await axios.post('http://localhost:8000/api/evento', {
        
        tituloEvento: tituloEvento,
        participacion:"asd",
        numParticipantes:0,
        numEntrenadores:0,
        descripcionEvento: descripcion,
        estadoEvento:true,
        aficheEvento:"asda",
        idTipoEvento:idTipoEvento,
        idAdministrador:"1"
      });
      console.log('Evento creado con éxito');
      setModalVisible(true);
      window.location.href = '/CrearEvento';
    
    } catch (error) {
      if (error.response) {
        
        console.error('Respuesta del servidor con error:', error.response);
        console.error('Código de estado HTTP:', error.response.status);
      } else if (error.request) {
        console.error('No se recibió respuesta del servidor:', error.request);
      } else {
        console.error('Error durante la configuración de la solicitud:', error.message);
      }

      setModalVisible(false);
      setError(true);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="tweet-composer">
      <h1 className="CrearEvento">Crear Evento</h1>
    <div className="PrimeraFila">
      <div className="TituloEvento">
        <div className='Campovacio'>
          <label htmlFor="tituloEvento">Nombre del evento:</label>
          <div className="ColorCampoVacio">*</div>
        </div>
        <input
          type="text"
          id="tituloEvento"
          name="tituloEvento"
          placeholder="Nombre"
          value={tituloEvento}
          onChange={(e) => setTituloEvento(e.target.value)}
          onBlur={() => setTituloEventoError(tituloEvento.trim() === "")}
          className={tituloEventoError ? "campo-vacio" : ""}
        />
        {(tituloEvento.length > 20 )&& <div className="ErrorForm">máximo 30 caracteres alfanuméricos</div>}

      </div>
      


      <div className="horaEvento">
        <div className="Campovacio">
          <label htmlFor="horaEvento">Hora de inicio</label>
          <div className="ColorCampoVacio">*</div>
        </div>
        <input
          type="time"
          id="horaEvento"
          name="horaEvento"
          value={horaEvento}
          onChange={(e) => setHoraEvento(e.target.value)}
          onBlur={() => setHoraEventoError(horaEvento.trim() === "00:00")}
          className={horaEventoError ? "campo-vacio" : "00:00"}
          required
        />
        {(horaEvento == "--:--"  )&& <div className="ErrorForm">Hora invalida</div>}

      </div>
    </div>

    <div className="Fecha">
        <div className="FechaInicio">
          <div className="Campovacio">
            <label htmlFor="fecha-inicio">Fecha inicio:</label>
            <div className="ColorCampoVacio">*</div>
          </div>
          <input
            className={`FechaDesing ${fechaInicioError ? "campo-vacio" : 'null'}`}
            type="date"
            id="fecha-inicio"
            name="fecha-inicio"
            value={fechaInicio}
            onChange={(e) => setFechaInicio(e.target.value)}
            onBlur={() => setFechaInicioError(fechaInicio.trim() === 'null')}
            required
          />
          {(fechaInicio == "" )&& <div className="ErrorForm">Fecha invalida</div>}

        </div>
        <div className="FechaFinal">
          <div className="Campovacio">
            <label htmlFor="fecha-fin">Fecha fin:</label>
            <div className="ColorCampoVacio">*</div>
          </div>         
          <input
            className={`FechaDesing ${fechaFinError ? "campo-vacio" : "null"}`}
            type="date"
            id="fecha-fin"
            name="fecha-fin"
            value={fechaFin}
            onChange={(e) => setFechaFin(e.target.value)}
            onBlur={() => setFechaFinError(fechaFin.trim() === "null")}
            required
          />
          {(fechaFin == "" )&& <div className="ErrorForm">Fecha invalida</div>}
        </div>
      </div>

      <div className="SegundaFila">
        
        <div className="composer-form">
          <div className='Campovacio'>
            <label htmlFor="idTipoEvento">Seleccione el tipo de evento:</label>
            <div className="ColorCampoVacio">*</div>
          </div>
          <select
            id="idTipoEvento"
            name="idTipoEvento"
            value={idTipoEvento}
            onChange={(e) => setIdTipoEvento(e.target.value)}
            onBlur={() => setIdTipoEventoError(idTipoEvento.trim() === "-- seleccione --")}
            className={idTipoEventoError ? "campo-vacio" : "-- seleccione --"}
            required
            >
            <option value="0">-- seleccione --</option>
            <option value="1">Competencia</option>
            <option value="2">Taller de Entrenamiento</option>
            <option value="3">Seminario</option>
            <option value="4">Feria</option>
            <option value="5">Convencion</option>
          </select>
        </div>

        <div className="Modalidad">
          <div className='Campovacio'>
            <label htmlFor="modalidad">Modalidad: </label>
            <div className="ColorCampoVacio">*</div>
          </div>
          <select
            id="modalidad"
            name="modalidad"
            value={modalidad}
            onChange={(e) => setModalidad(e.target.value)}
            onBlur={() => setModalidadError(modalidad.trim() === "-- seleccione --")}
            className={modalidadError ? "campo-vacio" : "-- seleccione --"}
            required
          >
            <option value="null">-- seleccione --</option>
            <option value="Virtual">Virtual</option>
            <option value="Presencial">Presencial</option>
            <option value="Hibrida">Hibrida</option>
          </select>
        </div>
      </div>          

      <div className="Campovacio">
        <label htmlFor="ubicacion">Ubicación</label>
        <div className="ColorCampoVacio">*</div>
      </div>
      
      <input
        type="text"
        id="ubicacion"
        name="ubicacion"
        placeholder="Ubicación"
        value={ubicacion}
        onChange={(e) => setUbicacion(e.target.value)}
        onBlur={() => setUbicacionError(ubicacion.trim() === "")}
        className={ubicacionError ? "campo-vacio" : ""}
        required
        
      />{(ubicacion.length > 60)&& <div className="ErrorForm">máximo 60 caracteres alfanuméricos</div>}
      
      <div className="Campovacio">
        <label htmlFor="descripcion">Descripción</label>
        <div className="ColorCampoVacio">*</div>
      </div>
      <textarea
        id="descripcion"
        name="descripcion"
        placeholder="Descripcion del evento"
        rows="4"
        value={descripcion}
        onChange={(e) => setDescripcion(e.target.value)}
        onBlur={() => setDescripcionError(descripcion.trim() === "")}
        className={descripcionError ? "campo-vacio" : ""}
        required
      >
        
      </textarea>
      

      {(descripcion.length > 250)&& <div className="ErrorForm">máximo 250 caracteres alfanuméricos</div>}
    

      <div className="container">
        <div className="CreadoExitosamente">
          {modalVisible && (
            <div >
              <span className="close" onClick={() => setModalVisible(false)}>
              </span>
              <div>Creado con exito!</div>
            </div>
          )}

          {(tituloEventoError || 
            fechaFinError || 
            fechaInicioError || 
            descripcionError || 
            horaEventoError || 
            idTipoEventoError || 
            modalidadError ||
            ubicacionError ||
            Error
          && <Alert/>)}
        </div>
        
        <div className="BotonPosicion">
          <button className="tweet-button" type="submit" onClick={handleButtonCancelarCE}>
            Cancelar
          </button>
          <button className="tweet-button" type="submit" onClick={handleSubmit}>
            Siguiente
          </button>
        </div>
      </div>
  </form>
  );
};

export default EventForm;
