import React, { useState, useEffect } from 'react';
import './assets/HomeVistaEvento.css';
import axios from '../components/api/conexionApi';

const HomeVistaEvento = () => {
  const [eventos, setEventos] = useState([]);

  useEffect(() => {
    const obtenerEventos = async () => {
      try {
        const response = await axios.get('./eventoConActividad');
        const data = response.data;
        setEventos(data.eventosConProgramaGeneral);
      } catch (error) {
        console.error('Error al obtener eventos:', error);
      }
    };

    obtenerEventos();
  }, []);

  const handleRegistrarseClick = async (idEvento) => {
    window.location.href = `./VerInformacionEvento?id=${idEvento}`;
  };

  return (
    <div className="cuerpo">
      <h3 className="titulo_home">Eventos en Curso</h3>
      <hr />

      <div className="eventos-container">
        {eventos.map((eventoConProgramaGeneral) => (
          <div key={eventoConProgramaGeneral.evento.idEvento} className="evento-rectangulo">
            <img src={eventoConProgramaGeneral.evento.aficheEvento} alt={`Evento ${eventoConProgramaGeneral.evento.idEvento}`} />

            <div className="evento-info">
              <h4>{eventoConProgramaGeneral.evento.tituloEvento}</h4>

              {/* Iterar sobre todas las actividades */}
              {eventoConProgramaGeneral.actividades.map((actividad, index) => (
                <div key={index}>
                  <p>Nombre de la Actividad: {actividad.nombreActividad}</p>
                  <p>Fecha de inicio: {actividad.fechaInicioActividad}</p>
                  <p>Fecha de fin: {actividad.fechaFinActividad}</p>
                  <p>Modalidad: {actividad.modalidad}</p>
                  <p>Ubicaci칩n: {actividad.ubicacionActividad}</p>
                </div>
              ))}

              {eventoConProgramaGeneral.evento.descripcionEvento && <p>Descripci칩n: {eventoConProgramaGeneral.evento.descripcionEvento}</p>}
              <p  className={`evento-rectangulo ${
              eventoConProgramaGeneral.evento.estadoEvento === 1 ? 'activo' : 'cancelado'
            }`}
          ></p>
              <button className="masinfo" onClick={() => handleRegistrarseClick(eventoConProgramaGeneral.evento.idEvento)}>
                M치s informaci칩n
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomeVistaEvento;
