import React, { useState, useEffect } from 'react';
import './assets/VerInformacionEvento.css'
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Importar estilos CSS


const CalendarioDesplegable = () => {
    const [fechaInicio, setFechaInicio] = useState(new Date());
    const [fechaFin, setFechaFin] = useState(new Date());
    const [calendarioVisible, setCalendarioVisible] = useState(false);
  
    const mostrarCalendario = () => {
      setCalendarioVisible(true);
    };
  
    const ocultarCalendario = () => {
      setCalendarioVisible(false);
    };
  
    const seleccionarFechaInicio = (date) => {
      setFechaInicio(date);
      setCalendarioVisible(false); // Ocultar el calendario después de seleccionar la fecha de inicio
    };
  
    const seleccionarFechaFin = (date) => {
      setFechaFin(date);
      setCalendarioVisible(false); // Ocultar el calendario después de seleccionar la fecha de fin
    };
  
    const dentroDelRango = ({ date }) => {
      return date >= fechaInicio && date <= fechaFin;
    };
  
    return (
      <div className='calendario-desplegable'>
        <div className='icono-calendario' onClick={mostrarCalendario}>
          <i className='fas fa-calendar'></i>
        </div>
        {calendarioVisible && (
          <div className='contenedor-calendario'>
            <Calendar
              onChange={seleccionarFechaInicio}
              selectRange={true}
              value={[fechaInicio, fechaFin]}
              tileContent={dentroDelRango}
              tileClassName='resaltado'
            />
            <button onClick={ocultarCalendario}>Cerrar Calendario</button>
          </div>
        )}
      </div>
    );
  };
  
  export default CalendarioDesplegable;
