import React, { useState, useEffect } from 'react';
import axios from './api/conexionApi';
import './assets/registroParticipantes.css';
import { Modal, Button } from 'react-bootstrap';

const idEvento=1;
const FormularioRegistroParticipantes = () => {

  const [datosPersona, setDatosPersona] = useState({
      idPersona: '',
      nombrePersona: '',
      apellidoPersona: '',
      genero: '',
      telefonoPersona: ''
    });

  const [datosParticipante, setDatosParticipante] = useState({
    idParticipante: '', // Nuevo campo
    idEquipo: '',
    idEvento: idEvento
  });

  const [responsePersona, setResponsePersona] = useState(null);

  const handleChangePersona = (e) => {
    const { name, value } = e.target;
    setDatosPersona({
      ...datosPersona,
      [name]: value,
    });
  };

  const handleChangeParticipante = (e) => {
    const { name, value } = e.target;
    setDatosParticipante({
      ...datosParticipante,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/storePersona', datosPersona);
      setResponsePersona(response);
    } catch (error) {
      console.error('Error al guardar Persona:', error);
    }
  };


  const [evento, setEvento] = useState(null);
  useEffect(() => {
      axios.get(`eventos/${EventoId}`) 
          .then(response => {
              setEvento(response.data.evento);
          })
          .catch(error => {
              console.error('Error al obtener el evento:', error);
          });
  }, []);

  useEffect(() => {
    if (responsePersona) {
      setDatosParticipante({
        ...datosParticipante,
        idParticipante: responsePersona.data.idPersona,
      });

      // Ahora, puedes enviar la solicitud para guardar el participante
      const guardarParticipante = async () => {
        try {
          await axios.post('/storeParticipante', datosParticipante);
        } catch (error) {
          console.error('Error al guardar participante:', error);
        }
      };

      guardarParticipante();
    }else{
      console.log
    }
  }, [responsePersona, datosParticipante]);
  
  return (
    <div>
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="idPersona">ID de Persona:</label>
        <input
          type="text"
          id="idPersona"
          name="idPersona"
          value={datosPersona.idPersona}
          onChange={handleChangePersona}
        />
      </div>
      <div>
        <label htmlFor="nombrePersona">Nombre:</label>
        <input
          type="text"
          id="nombrePersona"
          name="nombrePersona"
          value={datosPersona.nombrePersona}
          onChange={handleChangePersona}
        />
      </div>
      <div>
        <label htmlFor="apellidoPersona">Apellido:</label>
        <input
          type="text"
          id="apellidoPersona"
          name="apellidoPersona"
          value={datosPersona.apellidoPersona}
          onChange={handleChangePersona}
        />
      </div>
      <div>
        <label htmlFor="genero">Género:</label>
        <input
          type="text"
          id="genero"
          name="genero"
          value={datosPersona.genero}
          onChange={handleChangePersona}
        />
      </div>
      <div>
        <label htmlFor="telefonoPersona">Teléfono:</label>
        <input
          type="text"
          id="telefonoPersona"
          name="telefonoPersona"
          value={datosPersona.telefonoPersona}
          onChange={handleChangePersona}
        />
      </div>      
      <button type="submit">Guardar</button>
    </form>
  
 </div>
  );
}
export default FormularioRegistroParticipantes;
