import React, { useState, useEffect } from 'react';
import { Axios } from 'axios';
const EventosList = () => {
  const [eventos, setEventos] = useState([]);

  useEffect(() => {
    // Obtener la lista de eventos al cargar el componente
    fetch('/api/eventos')
      .then(response => response.json())
      .then(data => setEventos(data.eventos))
      .catch(error => console.error('Error al obtener eventos:', error));
  }, []);

  return (
    <div>
      <h1>Lista de Eventos</h1>
      <ul>
        {eventos.map(evento => (
          <Evento key={evento.id} evento={evento} />
        ))}
      </ul>
    </div>
  );
};

const Evento = ({ evento }) => {
  const [actividades, setActividades] = useState([]);

  useEffect(() => {
    // Obtener las actividades para el evento actual
    fetch(`/api/evento/${evento.id}/actividades`)
      .then(response => response.json())
      .then(data => setActividades(data.actividades))
      .catch(error => console.error(`Error al obtener actividades para el evento ${evento.id}:`, error));
  }, [evento.id]);

  return (
    <li>
      <h2>{evento.nombre}</h2>
      <p>Descripci√≥n: {evento.descripcion}</p>

      <h3>Actividades:</h3>
      <ul>
        {actividades.map(actividad => (
          <li key={actividad.id}>{actividad.nombre}</li>
        ))}
      </ul>
    </li>
  );
};

export default EventosList;
