import React, { useState } from 'react';
import axios from './api/conexionApi';
import './assets/registroParticipantes.css';
const evento=1;
const errorCaracteresNoPermitidos='Esta intentando ingresar un caracter no permitido';
const errorOchoCaracteres='Este campo no puede superar los 8 caracteres';
const errorTreintaCaracteres='Este campo no puede superar los 30 caracteres';
const errorSieteCaracteres='Este campo no puede ser menor los 7 caracteres';
const errorSoloNumeros='Solo se permiten caracteres numéricos';
const errorSeleccioneUnaOpcion='Seleccione al menos una opción';
const FormularioRegistroParticipantes = (idEvento) => {
  const [formData, setFormData] = useState({
    idPersona: '',
    nombrePersona: '',
    apellidoPersona: '',
    genero: '',
    telefonoPersona: ''
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [correoCorreo, setCorreoC] = useState('');
  const [errorMessages, setErrorMessages] = useState({
    nombrePersona: '',
    apellidoPersona: '',
    idPersona: '',
    genero: '',
    telefonoPersona: '',
    correoCorreo: '',
  });

const handleChangeCorreo = (e) => {
 const { value } = e.target; 
 if (/[^a-zA-Z0-9._@-]/.test(value)) {
    setErrorMessage("Solo permite estos caracteres especiales: (.), (-), (_) y (@)");
    return;
  }

  setErrorMessage('');
 setCorreoC(value);
// const newValueCorreo = e.target.value.replace(/[^a-zA-Z0-9._@-]/g, '');
 
/*setCorreoC(
 newValueCorreo
 );*/
};

  const handleChange = (e) => {
    const { name, value } = e.target;
    /*setFormData({
      ...formData,
      [name]: value
    });*/
    switch (name) {
        case 'nombrePersona':
            case 'apellidoPersona':
              // Permitir solo caracteres alfabéticos y espacios, con un límite de 30 caracteres
              const newValueAlpha = value.replace(/[^a-zA-Z\s]/g, '').substring(0, 30);
              
              if (value.length > 30) {
              //  setErrorMessage(`El campo ${name} no puede superar los 30 caracteres`);
              setErrorMessages({
                ...errorMessages,
                [name]: errorTreintaCaracteres,
              });
              } else if (newValueAlpha !== value) {
                setErrorMessages({
                    ...errorMessages,
                    [name]: errorCaracteresNoPermitidos,
                  });
               // setErrorMessage(`Caracteres no permitidos en ${name}`);
              } else {
                //setErrorMessage('');
                setErrorMessages({
                    ...errorMessages,
                    [name]: '',
                  });
              }
        
              setFormData({
                ...formData,
                [name]: newValueAlpha,
              });
              break;
  
        case 'idPersona':
          const newValueID = value.replace(/[^\d]/g, '').substring(0, 8);
          
      if (newValueID !== value) {
        setErrorMessages({
          ...errorMessages,
          idPersona: errorSoloNumeros,
        });
      } else if (value.length > 8) {
        setErrorMessages({
            ...errorMessages,
            idPersona: errorOchoCaracteres,
          });
        
      } else if (value.length < 7) {
        setErrorMessages({
            ...errorMessages,
            idPersona: errorSieteCaracteres,
          });
      } else {
        setErrorMessages({
          ...errorMessages,
          idPersona: '',
        });
      }

      setFormData({
        ...formData,
        [name]: newValueID,
      });
      break;
                
  
        case 'genero':
      if (value === '') {
        setErrorMessages({
          ...errorMessages,
          genero: errorSeleccioneUnaOpcion,
        });
      } else {
        setErrorMessages({
          ...errorMessages,
          genero: '',
        });
      }

      setFormData({
        ...formData,
        [name]: value,
      });
      break;
  
        case 'telefonoPersona':
          const newValueTel = value.replace(/[^\d]/g, '').substring(0, 8);
            if(va)

          setFormData({
            ...formData,
            [name]: newValueTel,
          });
          break;
  
        default:
          setFormData({
            ...formData,
            [name]: value,
          });
      }
  };
 
 
  
  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('./storePersona', formData)
      .then((a) => {
        
        console.log('Datos guardados correctamente', formData.idPersona);

        const correo = {
            correoC: correoCorreo,   
            estadoNotificacion: true,
            idPersona: formData.idPersona
          };
          console.log('Datos editados', correo.idPersona);

          axios.post('./correos', correo)
          .then((b)=>{

          console.log('Datos de correo guardados correctamente', correo.correoC);

          const formParticipante = {
            idParticipante: formData.idPersona,   
            idEvento: evento,
            idEquipo: null
          };
  
          console.log('Datos editados', formParticipante.idParticipante);
  
            axios.post('./storeParticipante', formParticipante)
            .then((b)=>{
  
            console.log('Datos de participante guardados correctamente', formParticipante.idParticipante);
  
            })
            .catch((error) => {
         
              console.error('Error al guardar participante ', error);
            });
            setFormData({
              idPersona: '',
              nombrePersona: '',
              apellidoPersona: '',
              genero: '',
              telefonoPersona: ''

            });
            setCorreoC('');


          })
          .catch((error) => {
       
            console.error('Error al guardar correo ', error);
          });
          
      })
      .catch((error) => {
       
        console.error('Error al guardar los datos', error);
      });
  };

  return (
    <div>
        <h1>Formulario de Registro de Participante</h1>
    <form onSubmit={handleSubmit}>
    
      
      <div>
        <label htmlFor="nombrePersona">Nombres:</label>
        <input
          type="text"
          id="nombrePersona"
          name="nombrePersona"
          value={formData.nombrePersona}
          onChange={handleChange}
        />
         {errorMessages.nombrePersona && (
          <div className='error-message'>{errorMessages.nombrePersona}</div>
        )}
      </div>
      <div>
        <label htmlFor="apellidoPersona">Apellido:</label>
        <input
          type="text"
          id="nombrePersona"
          name="apellidoPersona"
          value={formData.apellidoPersona}
          onChange={handleChange}
        />
          {(<div className='error-message'>{errorMessages.apellidoPersona}</div>
        )}
      </div>
      <div>
        <label htmlFor="idPersona">CI:</label>
        <input
          type="text"
          id="idPersona"
          name="idPersona"
          value={formData.idPersona}
          onChange={handleChange}          
        />
          {errorMessages.idPersona && 
        (<div className='error-message'>{errorMessages.idPersona}</div>
        )}
      </div>
      <div>
        <label htmlFor="genero">Género:</label>
        <select
          id="genero"
          name="genero"
          value={formData.genero}
          onChange={handleChange}
        >
          <option value="Femenino">Femenino</option>
          <option value="Masculino">Masculino</option>
        </select>
        {
        errorMessages.genero &&
        (<div className='error-message'>{errorMessages.genero}</div>)
        }
      </div>
      <div>
        <label htmlFor="telefonoPersona">Teléfono:</label>
        <input
          type="text"
          id="telefonoPersona"
          name="telefonoPersona"
          value={formData.telefonoPersona}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="correoC">correo:</label>
        <input
          type="text"
         
          value={correoCorreo}
          onChange={handleChangeCorreo}
        />
         {errorMessage && <div className='error-message'>{errorMessage}</div>}
      </div>
      


 
      
      <button type="submit">Guardar</button>
    </form>
    
    
      </div>


  );


}
export default FormularioRegistroParticipantes;
