import React, { useState, useEffect } from 'react';
import axios from './api/conexionApi';
import './assets/VerInformacionEvento.css'
//import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css'; // Importar estilos CSS
import CalendarioDesplegable from './CalendarioDesplegable';
const EventoId=3;
const nombreActividadBuscada = "Cronograma general";



const handleRegistrarseClick = () => {
    
    window.location.href = './registroParticipantes';
}
const EventoComponent = () => {
    //tabla evento
    const [evento, setEvento] = useState(null);
    useEffect(() => {
        axios.get(`eventos/${EventoId}`) 
            .then(response => {
                setEvento(response.data.evento);
            })
            .catch(error => {
                console.error('Error al obtener el evento:', error);
            });
    }, []);

    
              
    if (!evento) {
        return <div>Cargando evento...</div>;
    }
    
    if (!requisitos) {
        return <div>Cargando requisitos ...</div>;
    }

    if (!reglas) {
        return <div>Cargando reglas ...</div>;
    }

    if (!actividades) {
        return <div>Cargando actividades ...</div>;
    }
    
    if (!actividadEncontrada) {
        return <div>Cargando datos de cronograma del evento ...</div>;
    }else{
        
        console.log(" la actividad encontrada es")
        console.log(actividadEncontrada)
        console.log("el nombre es " ,actividadEncontrada.nombreActividad)  
    }
    
    const fechaInicio1 = new Date(actividadEncontrada.fechaInicioActividad);

    const fechaFin1 = new Date(actividadEncontrada.fechaFinActividad);
    const descripcionConSaltos = evento.descripcionEvento.split('\n').map((item, i) => (
        <p key={i}>{item}</p>
    ));
    return (
        <div className="all-info-container">
            <div className='info-box'>
                <h1>{evento.tituloEvento}</h1>
            </div>
           
            <br />

           
            <div className='info-box'>
                <strong>Fecha Inicio del evento </strong>
                <p>{actividadEncontrada.fechaInicioActividad}</p>
            </div>
  
           <br />

            <div className='info-box'>
                <strong>Fecha Fin del evento </strong>
                <p>{actividadEncontrada.fechaFinActividad}</p>
            </div>

            

            <br />

            <div className='info-box'>
                <strong>Hora inicio del evento </strong>
                <p>{actividadEncontrada.horaInicioActividad}</p>
          
                {actividades && <CalendarioDesplegable EventoId />}


            </div>

            <br />

            
            
            <div className='info-box'>
                <strong> modalidad del evento </strong>
                <p>{actividadEncontrada.modalidad}</p>
            </div>

            <br />

            <div className='info-box'>
                <strong>Ubicacion del evento </strong>
                <p>{actividadEncontrada.ubicacionActividad}</p>
            </div>

            <br />



            <div className='date-container'>
            
                    <div className='date-box'>
                    <strong>Estado:</strong>
                    <p className={`info-box ${evento.estadoEvento ? 'estado-activo' : 'estado-inactivo'}`}><strong>{evento.estadoEvento ? 'Activo' : 'Inactivo'}</strong></p>
                    </div>
                 
                    
            </div>
        
            <br />
            

            
            <div className='date-box'>
                <strong>Dinamica:</strong>  
                <p className='info-box'> {evento.participacion}</p>
        <br />

                {evento.participacion === 'Equipo' && (

                <div className='date-container'>
                    <>  

                    <div className='date-box'>
                        <strong>Cantidad de entrenadores:</strong>
                        <p className='info-box'> {evento.numEntrenadores}</p>
                    </div>
                    
                    <div className='date-box'>
                        <strong>Cantidad de participantes:</strong>
                        <p className='info-box'> {evento.numParticipantes}</p>
                    </div>
                </>
                </div>
            )}

             {evento.participacion === 'Individual' && (
        
            <div >
            
            </div>
                )}
            </div>


               
            <div className='info-box'>
                <strong>Descripcion:</strong>
                {descripcionConSaltos}
            </div>

        <br />

            <div  className='info-box'>
                                       
                        <strong>Reglas</strong>
                <ol>
                    {reglas.map(reglas => (
                    <li>
                        <strong> {reglas.nombreRegla}</strong> 
                        <p></p>
                        <strong>Descripción:</strong> 
                        <p>{reglas.descripcionRegla}</p>
                    </li>
                    ))}
                </ol>
                </div>

                <div  className='info-box'>
                <strong>Actividades</strong>
                <ol>
                    {actividades.map(actividad => (
                    <li>
                        
                        <strong>{actividad.nombreActividad}</strong> 
                        <p></p>
                        <strong>Descripción:</strong> 
                        <p>{actividad.descripcionActividad}</p>
                        <strong>Modalidad:</strong> 
                        <p>{actividad.modalidadActividad}</p>
                        <strong>Fecha Inicio Actividad:</strong> 
                        <p> {actividad.fechaFinActividad}</p>
                        <strong>Fecha fin Actividad:</strong> 
                        <p>{actividad.fechaFinActividad}</p>
                        <strong>Hora Inicio Actividad:</strong> 
                        <p> {actividad.horaInicioActividad}</p>
                        <strong>Ubicacion Actividad:</strong> 
                        <p>{actividad.ubicacionActividad}</p>
                    </li>
                    ))}
                </ol>
                </div>

                <div  className='info-box'>
                <strong>Requisitos</strong>
                <ol>
                    {requisitos.map(requisito => (
                    <li>
                        <div  className='info-box'>
                        <strong>{requisito.nombreRequisito}</strong> 
                        <p></p>
                        <strong>Descripción:</strong> 
                        <p>{requisito.descripcionRequisito}</p>
                        </div>
                    </li>
                    ))}
                </ol>

                    
            </div>
           


                       

            <div className="button-container">
           
                <button className="blue-button" onClick={handleRegistrarseClick}>Registrarse</button>

            
                <button className="blue-button" onClick={() => window.history.back()}>Volver Atrás</button>
            </div>


            
  

        </div>
        
    );
    
}

export default EventoComponent;
