import React, { useState, useEffect } from 'react';
import axios from './api/conexionApi';
import './assets/registroParticipantes.css';
import { Modal, Button } from 'react-bootstrap';

const idEvento=1;
// FormularioPersonaParticipante.js


class FormularioPersonaParticipante extends Component {
  constructor(props) {
    super(props);
    this.state = {
      idPersona: '',
      nombrePersona: '',
      apellidoPersona: '',
      genero: '',
      telefonoPersona: '',
      idParticipante: props.idParticipante || '', // Valor proporcionado como prop
      idEquipo: '',
      idEvento: props.idEvento || '', // Valor proporcionado como prop
    };
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Guardar en Persona
      const personaResponse = await fetch('/storePersona', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          idPersona: this.state.idPersona,
          nombrePersona: this.state.nombrePersona,
          apellidoPersona: this.state.apellidoPersona,
          genero: this.state.genero,
          telefonoPersona: this.state.telefonoPersona,
        }),
      });
      const personaData = await personaResponse.json();

      // Guardar en Participante
      const participanteResponse = await fetch('/storeParticipante', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          idParticipante: this.state.idParticipante || personaData.data.idPersona, // Usar idPersona si idParticipante no está definido
          idEquipo: this.state.idEquipo,
          idEvento: this.state.idEvento,
        }),
      });
      const participanteData = await participanteResponse.json();

      console.log('Persona creada:', personaData);
      console.log('Participante creado:', participanteData);

      // Puedes manejar la respuesta como desees
    } catch (error) {
      console.error('Error al crear persona y participante:', error);
      // Puedes manejar el error como desees
    }
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        {/* Campos para Persona */}
        <label>
          ID Persona:
          <input
            type="text"
            name="idPersona"
            value={this.state.idPersona}
            onChange={this.handleChange}
          />
        </label>
        <label>
          Nombre Persona:
          <input
            type="text"
            name="nombrePersona"
            value={this.state.nombrePersona}
            onChange={this.handleChange}
          />
        </label>
        {/* Otros campos para Persona */}
        
        {/* Campos para Participante */}
        <label>
          ID Equipo:
          <input
            type="text"
            name="idEquipo"
            value={this.state.idEquipo}
            onChange={this.handleChange}
          />
        </label>
        <label>
          ID Evento:
          <input
            type="text"
            name="idEvento"
            value={this.state.idEvento}
            onChange={this.handleChange}
          />
        </label>
        {/* Otros campos para Participante */}

        <button type="submit">Guardar Persona y Participante</button>
      </form>
    );
  }
}

export default FormularioPersonaParticipante;
