// Formulario.jsx
import React, { useState } from 'react';
import axios from '../components/api/conexionApi';

const Formulario = ({ onSubmit, tipo }) => {
  const [nombrePersona, setNombrePersona] = useState('');
  const [apellidoPersona, setApellidoPersona] = useState('');
  const [genero, setGenero] = useState('');
  const [telefonoPersona, setTelefonoPersona] = useState('');
  const [correoC, setCorreo] = useState('');
  const [estadoNotificacion, setEstadoCorreo] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (tipo === 'persona') {
        const response = await axios.post('./storePersona', {
          nombrePersona,
          apellidoPersona,
          genero,
          telefonoPersona,
        });

        const personaData = response.data.data;

        // Guardar el objeto persona en el estado
        onSubmit({ idPersona: personaData.idPersona });
      } else {
        // Envía los datos de correo (asumiendo que los datos de persona ya están guardados)
        const response = await axios.post('./correos', {
          idPersona: onSubmit.idPersona,
          correoC,
          estadoNotificacion,
        });

        console.log(response.data);
      }
    } catch (error) {
      console.error('Error al enviar datos al servidor', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {tipo === 'persona' && (
        <>
          <label>
            Nombre:
            <input
              type="text"
              value={nombrePersona}
              onChange={(e) => setNombrePersona(e.target.value)}
            />
          </label>
          <label>
            Apellido:
            <input
              type="text"
              value={apellidoPersona}
              onChange={(e) => setApellidoPersona(e.target.value)}
            />
          </label>
          <label>
            Genero:
            <input
              type="text"
              value={genero}
              onChange={(e) => setGenero(e.target.value)}
            />
          </label>
          <label>
            Teléfono:
            <input
              type="text"
              value={telefonoPersona}
              onChange={(e) => setTelefonoPersona(e.target.value)}
            />
          </label>
        </>
      )}

      {tipo === 'correo' && (
        <>
          <label>
            Correo:
            <input
              type="text"
              value={correoC}
              onChange={(e) => setCorreo(e.target.value)}
            />
          </label>
          <label>
            Estado del Correo:
            <input
              type="text"
              value={estadoNotificacion}
              onChange={(e) => setEstadoCorreo(e.target.value)}
            />
          </label>
        </>
      )}

      <button type="submit">Guardar {tipo === 'persona' ? 'Persona' : 'Correo'}</button>
    </form>
  );
};

export default Formulario;
