import React, { useState } from 'react';

const InnerModal = ({ isOpen, onClose }) => {
  const handleClose = () => {
    onClose();
  };

  return (
    <div className={isOpen ? 'inner-modal' : 'hidden'}>
      <h2>Contenido del Modal Interno</h2>
      <button onClick={handleClose}>Cerrar Modal Interno</button>
    </div>
  );
};

const ModalChild = ({ onCloseSelf, onCloseParent }) => {
  const [isInnerModalOpen, setInnerModalOpen] = useState(false);

  const handleSelfClose = () => {
    onCloseSelf();
  };

  const handleParentClose = () => {
    onCloseParent();
  };

  const openInnerModal = () => {
    setInnerModalOpen(true);
  };

  const closeInnerModal = () => {
    setInnerModalOpen(false);
  };

  return (
    <div>
      <h2>Contenido del Modal Hijo</h2>
      <button onClick={handleSelfClose}>Cerrar Modal Hijo</button>
      <button onClick={handleParentClose}>Cerrar Modal Padre</button>
      <button onClick={openInnerModal}>Mostrar Modal Interno</button>

      {/* Nuevo Modal Anidado */}
      <InnerModal isOpen={isInnerModalOpen} onClose={closeInnerModal} />
    </div>
  );
};

const ModalParent = () => {
  const [isModalOpen, setModalOpen] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  return (
    <div>
      <button onClick={openModal}>Abrir Modal Padre</button>

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            {/* Pasa la funci√≥n openInnerModal al modal hijo */}
            <ModalChild onCloseSelf={closeModal} onCloseParent={closeModal} openInnerModal={openModal} />
          </div>
        </div>
      )}
    </div>
  );
};

const App = () => {
  return (
    <div>
      <ModalParent />
    </div>
  );
};

export default App;
