import React, { useState } from 'react';
import axios from 'axios';
import './assets/configurarEvento.css';

const ConfiguracionEvento = () => {
  const [opcionesSeleccionadas, setOpcionesSeleccionadas] = useState([]);
  const [mostrarSubopciones, setMostrarSubopciones] = useState(false);
  const [subopcionSeleccionada, setSubopcionSeleccionada] = useState('');

  const opciones = [
    'Opción 1',
    'Opción 2',
    'Opción 3',
    'Opción 4',
    'Opción 5',
    'Opción 6',
    'Opción 7',
    'Participantes',
  ];

  const handleOpcionClick = (opcion) => {
    if (opcion === 'Participantes') {
      // Si se selecciona la opción 'Participación', mostramos las subopciones.
      setMostrarSubopciones(true);
    } else {
      if (opcionesSeleccionadas.includes(opcion)) {
        setOpcionesSeleccionadas(opcionesSeleccionadas.filter((item) => item !== opcion));
      } else {
        setOpcionesSeleccionadas([...opcionesSeleccionadas, opcion]);
      }
    }
  };

  const handleSubopcionChange = (subopcion) => {
    // Lógica para seleccionar una de las subopciones.
    setSubopcionSeleccionada(subopcion);
  };

  return (
    <div className="formulario-container">
      <h2>Campos requeridos para el evento</h2>
      <div className="columnas-container">
        <div className="columna">
          {opciones.slice(0, 4).map((opcion) => (
            <label key={opcion}>
              <input
                type="checkbox"
                checked={opcionesSeleccionadas.includes(opcion)}
                onChange={() => handleOpcionClick(opcion)}
              /> {opcion}
            </label>
          ))}
        </div>
        <div className="columna">
          {opciones.slice(4, 8).map((opcion) => (
            <label key={opcion}>
              <input
                type="checkbox"
                checked={opcionesSeleccionadas.includes(opcion)}
                onChange={() => handleOpcionClick(opcion)}
              /> {opcion}
            </label>
          ))}
        </div>
      </div>

      {mostrarSubopciones && (
        <div>
          <h3>Tipo de participación:</h3>
          <label>
            <input
              type="radio"
              checked={subopcionSeleccionada === 'Individual'}
              onChange={() => handleSubopcionChange('Individual')}
            /> Individual
          </label>
          <label>
            <input
              type="radio"
              checked={subopcionSeleccionada === 'Subopción 2'}
              onChange={() => handleSubopcionChange('Subopción 2')}
            /> Subopción 2
          </label>
        </div>
      )}

      <div className="botones-container">
        <button className="boton-cancelar">Cancelar</button>
        <button className="boton-siguiente">Siguiente</button>
      </div>
    </div>
  );
};

export default ConfiguracionEvento;
