import React, { useState } from 'react';
import axios from './api/conexionApi';
import './assets/registroParticipantes.css';
import { Modal, Button } from 'react-bootstrap';
const FormularioRegistroParticipantes = (idEvento) => {
  const [formData, setFormData] = useState({
    idPersona: '',
    nombrePersona: '',
    apellidoPersona: '',
    genero: '',
    telefonoPersona: ''
  });

  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  const handleShowModal = (message) => {
    setModalMessage(message);
    setShowModal(true);
  };

  const handleCloseModal = () => setShowModal(false);



  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('./postPersona', formData)
      .then((response) => {
        handleShowModal('Datos guardados correctamente');
        console.log('Datos guardados correctamente', response);
      })
      .catch((error) => {
        handleShowModal('Error al guardar los datos');
        console.error('Error al guardar los datos', error);
      });
  };

  return (
    <div>
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="idPersona">ID de Persona:</label>
        <input
          type="text"
          id="idPersona"
          name="idPersona"
          value={formData.idPersona}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="nombrePersona">Nombre:</label>
        <input
          type="text"
          id="nombrePersona"
          name="nombrePersona"
          value={formData.nombrePersona}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="apellidoPersona">Apellido:</label>
        <input
          type="text"
          id="apellidoPersona"
          name="apellidoPersona"
          value={formData.apellidoPersona}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="genero">Género:</label>
        <input
          type="text"
          id="genero"
          name="genero"
          value={formData.genero}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="telefonoPersona">Teléfono:</label>
        <input
          type="text"
          id="telefonoPersona"
          name="telefonoPersona"
          value={formData.telefonoPersona}
          onChange={handleChange}
        />
      </div>

      <Button variant="contained" onClick={handleShowModal}>Mostrar Modal</Button>

<Modal open={showModal} onClose={handleCloseModal}>
  <div className="modal-content">
    <h2>Confirmación</h2>
    <p>{modalMessage}</p>
    <Button variant="contained" onClick={handleCloseModal}>Cerrar</Button>
  </div>
</Modal>
      
      <button type="submit">Guardar</button>
    </form>
    
    
      </div>


  );


}
export default FormularioRegistroParticipantes;
