import React, { useState } from 'react';
import axios from 'axios';

const EventoFormulario = () => {
  const [titulo, setTitulo] = useState('');
  const [fechaHora, setFechaHora] = useState('');
  const [ubicacion, setUbicacion] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [iconoTipo, setIconoTipo] = useState(null);
  const [privacidad, setPrivacidad] = useState('publico');

  const handleTituloChange = (e) => {
    setTitulo(e.target.value);
  };

  const handleFechaHoraChange = (e) => {
    setFechaHora(e.target.value);
  };

  const handleUbicacionChange = (e) => {
    setUbicacion(e.target.value);
  };

  const handleDescripcionChange = (e) => {
    setDescripcion(e.target.value);
  };

  const handleIconoTipoChange = (e) => {
    // Manejar la lógica para el archivo de imagen aquí si es necesario
    // setIconoTipo(e.target.files[0]);
  };

  const handlePrivacidadChange = (e) => {
    setPrivacidad(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('titulo', titulo);
      formData.append('fechaHora', fechaHora);
      formData.append('ubicacion', ubicacion);
      formData.append('descripcion', descripcion);
      formData.append('iconoTipo', iconoTipo);
      formData.append('privacidad', privacidad);

      await axios.post('http://localhost:8000/api/eventos', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log('Evento creado con éxito');
    } catch (error) {
      console.error('Error al crear evento', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Título:
        <input
          type="text"
          value={titulo}
          onChange={handleTituloChange}
          required
        />
      </label>
      <label>
        Fecha y Hora:
        <input
          type="datetime-local"
          value={fechaHora}
          onChange={handleFechaHoraChange}
          required
        />
      </label>
      <label>
        Ubicación:
        <input
          type="text"
          value={ubicacion}
          onChange={handleUbicacionChange}
        />
      </label>
      <label>
        Descripción:
        <textarea
          value={descripcion}
          onChange={handleDescripcionChange}
          rows="4"
          required
        />
      </label>
      <label>
        Icono Tipo (Opcional):
        <input
          type="file"
          onChange={handleIconoTipoChange}
          accept="image/*"
        />
      </label>
      <label>
        Privacidad:
        <select value={privacidad} onChange={handlePrivacidadChange}>
          <option value="publico">Público</option>
          <option value="privado">Privado</option>
        </select>
      </label>
      <button type="submit">Crear Evento</button>
    </form>
  );
};

export default EventoFormulario;
