import React, { useState } from 'react';
import st from'./assets/ModalPersonaEncontrada.css';

// Importa la función para enviar el correo
import enviarCorreo from './utils/EnviarCorreo'; // Ajusta la ruta según sea necesario

const Modal = ({ onClose, handleYes, handleNo, datos }) => {
  const [codigo, setCodigo] = useState('');
  const [codigoEnviado, setCodigoEnviado] = useState(false);
  const [errorCodigo, setErrorCodigo] = useState('');
  const [codigoIngresado, setCodigoIngresado] = useState('');
  const [mostrarInput, setMostrarInput] = useState(false);

  // Función para enviar el código al correo
  const generarCodigo = () => {
    // Genera un número entre 0 y 99999, luego convierte a cadena
    const codigo = Math.floor(Math.random() * 100000).toString();
  
    // Asegura que el código tenga 5 dígitos, rellenando con ceros si es necesario
    return codigo.padStart(5, '0');
  };
  
  const enviarCodigo = async () => {
    const nuevoCodigo = generarCodigo();
    setCodigo(nuevoCodigo);
    setErrorCodigo(''); // Resetear cualquier error previo
    setCodigoIngresado(''); // Limpiar el campo de código
  
    try {
      await enviarCorreo(datos.correo, 'Tu Código de Verificación', `Tu código es: ${nuevoCodigo}`);
      setCodigoEnviado(true);
    } catch (error) {
      console.error('Error al enviar el correo', error);
    }
  };
  

  // Función para manejar la verificación del código
  const verificarCodigo = (e) => {
    e.preventDefault(); // Prevenir el comportamiento predeterminado
  
    if (codigo === codigoIngresado) {
      handleYes();
      
    } else {
      setErrorCodigo('El código ingresado no es correcto.');
      setCodigoEnviado(false); // Aquí restablecemos codigoEnviado
      setCodigoIngresado('');    }
  };

  if (!isOpen) {
    return null;
  }

  return (
    <div className='modal-containerMPE'>
      <div className='modal-contentMPE'>
        <h2>¿Ud. ya se ha registrado a un evento ICPC antes?</h2>
        <strong> Este es su correo: {datos.correo}</strong>
        <strong> Si es asi, por favor presione en "Enviar código" para completar tus datos</strong>

        {!codigoEnviado ? (
          <>
          <br/>
            <button type="button"  className='boton-ok' onClick={enviarCodigo}>Enviar código</button>
            <button type="button"  className='boton-ok' onClick={handleNo}>No</button>
          </>
        ) : mostrarInput && (
          <>
            <input 
              className='inputCodigo' 
              type="text" 
              placeholder="Ingrese el código" 
              value={codigoIngresado} 
              onChange={(e) => setCodigoIngresado(e.target.value)} 
            />
<button type="button" className='boton-ok' onClick={(e) => verificarCodigo(e)}>Verificar Código</button>
<button type="button"  className='boton-ok' onClick={handleNo}>Salir</button>

          </>
        )}
         
         {errorCodigo && <p className="error">{errorCodigo}</p>}
    {!codigoEnviado && (
      <button type="button" className='boton-ok' onClick={enviarCodigo}>Volver a enviar código</button>
    )}
        <span className="cerrar" onClick={closeModal}>&times;</span>
      </div>
    </div>
  );
};

export default Modal;
