import React, { useState, useEffect } from 'react';
import './assets/HomeVistaEvento.css';
import axios from '../components/api/conexionApi';
import Afiche from './imgs/competencia.jpg';
import botonIzquierda from './imgs/flechaIzquierda.png';
import botonDerecha from './imgs/correcta.png';
import Slider from 'react-slick';

const HomeVistaEvento = () => {
  const [eventos, setEventos] = useState([]);

  useEffect(() => {
    const fetchEventos = async () => {
      try {
        const response = await axios.get('./index');
        setEventos(response.data.eventos);
      } catch (error) {
        console.error('Error al obtener eventos:', error);
      }
    };

    fetchEventos();
  }, []);

  const handleRegistrarseClick = async () => {
    try {
      const response = await axios.get('./obtenerUltimoIdEvento');
      const ultimoId = response.data.ultimoId;
      window.location.href = `./VerInformacionEvento?id=${ultimoId}`;
    } catch (error) {
      console.error('Error al obtener el último ID del evento:', error);
    }
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    centerMode: true,
    centerPadding: '0',
  };

  return (
    <div className="body">
      <h3 className="titulo_home">Título evento</h3>
      <hr />

      <Slider {...settings}>
        {eventos.map(evento => (
          <div key={evento.idEvento} className="evento-slide">
            <img src={evento.aficheEvento} alt={`Evento ${evento.idEvento}`} />
            <div className="evento-info">
              <h4>{evento.tituloEvento}</h4>
              <p>Fecha de inicio: {evento.fechaInicio}</p>
              <p>Fecha de fin: {evento.fechaFin}</p>
              {evento.descripcionEvento && <p>Descripción: {evento.descripcionEvento}</p>}
              <p>Estado: {evento.estadoEvento === 1 ? 'Activo' : 'Inactivo'}</p>
            </div>
          </div>
        ))}
      </Slider>

      <div className="boton_mas_informacion">
        <button className="masinfo" type="submit" onClick={handleRegistrarseClick}>
          Más información
        </button>
      </div>
    </div>
  );
};

export default HomeVistaEvento;
