import React, { useState } from 'react';
import axios from 'axios';
import './formulario.css';

const EventForm = () => {
  const [titulo, setTitulo] = useState('Prueba1');
  const [fechaInicio, setFechaInicio] = useState('2023-10-15');
  const [fechaFin, setFechaFin] = useState('2023-10-20');
  //const [ubicacion, setUbicacion] = useState(''); //este no sirve aun 
  const [descripcion, setDescripcion] = useState('Descripción del evento');
  const [estado, setEstado] = useState('true');
  const [requisitos, setRequisitos] = useState('Requisitos del evento');
  const [horaEvento, setHoraEvento] = useState('15:00');
  const [imagen, setImagen] = useState('URL del afiche del evento');
  const [idAdministrador, setIdAdministrador] = useState('1');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  
  const handleTituloChange = (e) => setTitulo(e.target.value);
  const handleFechaInicioChange = (e) => setFechaInicio(e.target.value);
  const handleFechaFinChange = (e) => setFechaFin(e.target.value);
  //const handleUbicacionChange = (e) => setUbicacion(e.target.value);
  const handleDescripcionChange = (e) => setDescripcion(e.target.value);
  const handleEstadoChange = (e) => setEstado(e.target.value);
  const handleRequisitosChange = (e) => setRequisitos(e.target.value);
  const handleHoraEventoChange = (e) => setHoraEvento(e.target.value);
  const handleImagenChange = (e) => setImagen(e.target.value);
  const handleIdAdministradorChange = (e) => setIdAdministrador(e.target.value);
  const handleSuccessMessageChange = (message) => setSuccessMessage(message);
  const handleErrorMessageChange = (message) => setErrorMessage(message);
  
  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('titulo', titulo);
    formData.append('fechaInicio', fechaInicio);
    formData.append('fechaFin', fechaFin);
    formData.append('ubicacion', ubicacion);
    formData.append('descripcion', descripcion);
    formData.append('requisitos', requisitos);
    formData.append('TipoDeEvento', privacidad);
    formData.append('horaEvento', horaEvento);
    formData.append('imagen', imagen);
    formData.append('idPersona', idPersona);

    try {
      await axios.post('http://localhost:8000/api/evento', formData);
      setSuccessMessage('Evento creado con éxito'); // Establece el mensaje de éxito
      setErrorMessage(''); // Limpia el mensaje de error
      console.log('Evento creado con éxito');
    } catch (error) {
      setSuccessMessage(''); // Limpia el mensaje de éxito
      setErrorMessage('Error al crear evento'+ error); // Establece el mensaje de erro
    }


    try {
      await axios.post('http://localhost:8000/api/correos', {
        correoC: email,
        estadoNotificacion: activarNotificacion,
        idPersona: idPersona, // Agregar el valor del idPersona
      });

      console.log('Correo creado con éxito');
    } catch (error) {
      console.error('Error al crear correo', error);
    }

  };
  return (
    <div>
      <div className="success-message">{successMessage}</div>
      <div className="error-message">{errorMessage}</div>
      <form onSubmit={handleSubmit} className="tweet-composer">
        <div className="composer-form">
          {/* Título */}
          <label htmlFor="titulo">Título del Evento</label>
          <input
            type="text"
            id="titulo"
            name="titulo"
            placeholder="Título"
            value={titulo}
            onChange={handleTituloChange}
            required
          />

          {/* Fechas de inicio y fin */}
          <div className="Fecha">
            {/* Fecha de inicio */}
            <div className="FechaInicio">
              <label htmlFor="fecha-inicio">Fecha Inicio</label>
              <input
                className="FechaDesing"
                type="date"
                id="fecha-inicio"
                name="fecha-inicio"
                value={fechaInicio}
                onChange={handleFechaInicioChange}
                required
              />
            </div>

            {/* Fecha de fin */}
            <div className="FechaFinal">
              <label htmlFor="fecha-fin">Fecha Fin</label>
              <input
                className="FechaDesing"
                type="date"
                id="fecha-fin"
                name="fecha-fin"
                value={fechaFin}
                onChange={handleFechaFinChange}
                required
              />
            </div>
          </div>

          {/* Ubicación */}
          <label htmlFor="ubicacion">Ubicación</label>
          <input
            type="text"
            id="ubicacion"
            name="ubicacion"
            placeholder="Ubicación"
            value={ubicacion}
            onChange={handleUbicacionChange}
          />

          {/* Descripción */}
          <label htmlFor="descripcion">Descripción</label>
          <textarea
            id="descripcion"
            name="descripcion"
            placeholder="Descripción"
            rows="4"
            value={descripcion}
            onChange={handleDescripcionChange}
            required
          ></textarea>

          {/* Requisitos */}
          <label htmlFor="requisitos">Requisitos del evento</label>
          <textarea
            id="requisitos"
            name="requisitos"
            placeholder="Requisitos"
            rows="4"
            value={requisitos}
            onChange={handleRequisitosChange}
            required
          ></textarea>

          {/* Tipo de evento y hora */}
          <div className="img1">
            {/* Tipo de evento */}
            <div className="tipoEvento">
              <label htmlFor="privacidad">Tipo de evento</label>
              <select
                id="privacidad"
                name="privacidad"
                value={privacidad}
                onChange={handlePrivacidadChange}
              >
                <option value="Competición">Competición</option>
                <option value="Taller">Taller</option>
              </select>
            </div>

            {/* Hora del evento */}
            <div className="horaEvento">
              <label htmlFor="horaEvento">Hora</label>
              <input
                type="time"
                id="horaEvento"
                name="horaEvento"
                value={horaEvento}
                onChange={handleHoraEventoChange}
                required
              />
            </div>
          </div>

          {/* Subir imagen */}
          <div>
            <input
              type="file"
              id="icono-tipo"
              name="icono-tipo"
              accept="image/*"
              onChange={handleImagenChange}
            />
            {imagen && <img id="imagen-preview" src={URL.createObjectURL(imagen)} alt="Vista previa de la imagen" />}
          </div>

          {/* Botones de cancelar y guardar */}
          <div className="container">
            <div className="other-elements"></div>
            <div>
              <button className="tweet-button" type="button">
                Cancelar
              </button>
              <button className="tweet-button" type="submit">
                Guardar
              </button>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default EventForm;
