import React, { useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import   '../components/assets/MyCalendar.css'
const localizer = momentLocalizer(moment);

const MiCalendario = ({ startDate, endDate }) => {
  const [eventos, setEventos] = useState([
    {
      title: 'Evento 1',
      start: new Date(2023, 11, 20, 10, 0), // Fecha y hora de inicio
      end: new Date(2024, 0, 10, 12, 0),   // Fecha y hora de fin
    },
    // Agrega más eventos según tus necesidades
  ]);
  const eventStyleGetter = (event) => {
    if (event.start >= startDate && event.end <= endDate) {
      return {
        style: {
          backgroundColor: 'pink', // Cambia el color de fondo a rosa
        },
      };
    }

   
  };

  return (
    <div className="mi-contenedor">
    <Calendar
      localizer={localizer}
      events={eventos}
      startAccessor="start"
      endAccessor="end"
      eventPropGetter={eventStyleGetter}
      className="mi-calendario"
      views={['month', 'agenda']} // Define las vistas que deseas mostrar
      toolbar={true} // Habilita la barra de herramientas
    />
  </div>
  );
};

export default MiCalendario;
