import React, { useState } from 'react';
import axios from './api/conexionApi';
import './assets/registroParticipantes.css';
const evento=1;
const FormularioRegistroParticipantes = (idEvento) => {
  const [formData, setFormData] = useState({
    idPersona: '',
    nombrePersona: '',
    apellidoPersona: '',
    genero: '',
    telefonoPersona: ''
  });

  const [formParticipante, setFormParticipante] = useState({
    idParticipante:'',   
    idEvento:''  ,
    idEquipo:''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('./storePersona', formData)
      .then((a) => {
       
        console.log('Datos guardados correctamente', formData.nombrePersona);

        setFormParticipante({
          ...formParticipante,
          
          idParticipante: formData.idPersona,
          idEvento:evento,
          idEquipo:null

        });
        console.log('Datos editados', formParticipante.idEvento);

          axios.post('./storeParticipante', formParticipante)
          .then((b)=>{
            console.log('Datos de participante guardados correctamente', formParticipante.idParticipante);

          })
          .catch((error) => {
       
            console.error('Error al guardar participante ', error);
          });
          
      })
      .catch((error) => {
       
        console.error('Error al guardar los datos', error);
      });
  };

  return (
    <div>
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="idPersona">ID de Persona:</label>
        <input
          type="text"
          id="idPersona"
          name="idPersona"
          value={formData.idPersona}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="nombrePersona">Nombre:</label>
        <input
          type="text"
          id="nombrePersona"
          name="nombrePersona"
          value={formData.nombrePersona}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="apellidoPersona">Apellido:</label>
        <input
          type="text"
          id="apellidoPersona"
          name="apellidoPersona"
          value={formData.apellidoPersona}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="genero">GÃ©nero:</label>
        <input
          type="text"
          id="genero"
          name="genero"
          value={formData.genero}
          onChange={handleChange}
        />
      </div>
      <div>
        <label htmlFor="telefonoPersona">TelÃ©fono:</label>
        <input
          type="text"
          id="telefonoPersona"
          name="telefonoPersona"
          value={formData.telefonoPersona}
          onChange={handleChange}
        />
      </div>

      


 
      
      <button type="submit">Guardar</button>
    </form>
    
    
      </div>


  );


}
export default FormularioRegistroParticipantes;
