import React, { useState } from 'react';
import axios from 'axios';
import './formulario.css';

const EventForm = () => {
  const [titulo, setTitulo] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [ubicacion, setUbicacion] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [privacidad, setPrivacidad] = useState('Competición');
  const [requisitos, setRequisitos] = useState('');
  const [horaEvento, setHoraEvento] = useState('');
  const [imagen, setImagen] = useState(null);
  const [idPersona, setIdPersona] = useState('');

  const handleImagenChange = (e) => {
    // Validar que se seleccione una imagen
    const file = e.target.files[0];
    if (file) {
      // Validar tipo de archivo (opcional)
      if (!file.type.startsWith('image/')) {
        console.error('Por favor, seleccione una imagen.');
        return;
      }
      setImagen(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('titulo', titulo);
    formData.append('fechaInicio', fechaInicio);
    formData.append('fechaFin', fechaFin);
    formData.append('ubicacion', ubicacion);
    formData.append('descripcion', descripcion);
    formData.append('requisitos', requisitos);
    formData.append('TipoDeEvento', privacidad);
    formData.append('horaEvento', horaEvento);
    if (imagen) {
      formData.append('imagen', imagen);
    }
    formData.append('idPersona', idPersona);

    try {
      // Enviar solicitud POST a la API
      const response = await axios.post('http://localhost:8000/api/eventos', formData, {
        headers: {
          'Content-Type': 'multipart/form-data', // Establecer tipo de contenido para archivos
        },
      });

      // Manejar la respuesta del servidor (puede incluir mensajes de éxito o error)
      console.log(response.data.message);
      console.log('Evento creado con éxito');
    } catch (error) {
      // Manejar errores de la solicitud
      console.error('Error al crear evento', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="tweet-composer">
      <div className="composer-form">
        <label htmlFor="titulo">Título del Evento</label>
        <input
          type="text"
          id="titulo"
          name="titulo"
          placeholder="Título"
          value={titulo}
          onChange={handleTituloChange}
          required
        />

        <div className="Fecha">
          <div className="FechaInicio">
            <label htmlFor="fecha-inicio">Fecha Inicio</label>
            <input
              className="FechaDesing"
              type="date"
              id="fecha-inicio"
              name="fecha-inicio"
              value={fechaInicio}
              onChange={handleFechaInicioChange}
              required
            />
          </div>

          <div className="FechaFinal">
            <label htmlFor="fecha-fin">Fecha Fin</label>
            <input
              className="FechaDesing"
              type="date"
              id="fecha-fin"
              name="fecha-fin"
              value={fechaFin}
              onChange={handleFechaFinChange}
              required
            />
          </div>
        </div>

        <label htmlFor="ubicacion">Ubicación</label>
        <input
          type="text"
          id="ubicacion"
          name="ubicacion"
          placeholder="Ubicación"
          value={ubicacion}
          onChange={handleUbicacionChange}
        />

        <label htmlFor="descripcion">Descripción</label>
        <textarea
          id="descripcion"
          name="descripcion"
          placeholder="Descripción"
          rows="4"
          value={descripcion}
          onChange={handleDescripcionChange}
          required
        ></textarea>

        <label htmlFor="requisitos">Requisitos del evento</label>
        <textarea
          id="requisitos"
          name="requisitos"
          placeholder="Requisitos"
          rows="4"
          value={requisitos}
          onChange={handleRequisitosChange}
          required
        ></textarea>

        <div className="img1">
          <div className="tipoEvento">
            <label htmlFor="privacidad">Tipo de evento</label>
            <select
              id="privacidad"
              name="privacidad"
              value={privacidad}
              onChange={handlePrivacidadChange}
            >
              <option value="Competición">Competición</option>
              <option value="Taller">Taller</option>
            </select>
          </div>

          <div className="horaEvento">
            <label htmlFor="horaEvento">Hora</label>
            <input
              type="time"
              id="horaEvento"
              name="horaEvento"
              value={horaEvento}
              onChange={handleHoraEventoChange}
              required
            />
          </div>
        </div>

        <div>
          <input
            type="file"
            id="icono-tipo"
            name="icono-tipo"
            accept="image/*"
            onChange={handleImagenChange}
          />
          {imagen && <img id="imagen-preview" src={URL.createObjectURL(imagen)} alt="Vista previa de la imagen" />}
        </div>

        <div className="container">
          <div className="other-elements"></div>
          <div>
            <button className="tweet-button" type="button">
              Cancelar
            </button>
            <button className="tweet-button" type="submit">
              Guardar
            </button>
          </div>
        </div>
      </div>
    </form>
  );
};

export default EventForm;
