import React, { useState } from 'react';
import axios from 'axios';
import './formulario.css';

const EventForm = () => {
  const [tituloEvento, setTitulo] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [ubicacion, setUbicacion] = useState('');
  const [descripcion, setDescripcion] = useState('');
  //const [privacidad, setPrivacidad] = useState('Competición'); // Establecer valor inicial para privacidad
  const [requisitos, setRequisitos] = useState('');
  const [estado, setStado] = useState('');
  const [horaEvento, setHoraEvento] = useState('');
  const [imagen, setImagen] = useState('asd');
  const [idAdministrador, setidAdministrador] = useState(''); // Nuevo estado para idPersona

  const handleTituloChange = (e) => {
    setTitulo(e.target.value);
  };


  const handleFechaInicioChange = (e) => {
    setFechaInicio(e.target.value);
  };

  const handleFechaFinChange = (e) => {
    setFechaFin(e.target.value);
  };

  const handleUbicacionChange = (e) => {
    setUbicacion(e.target.value);
  };

  const handleDescripcionChange = (e) => {
    setDescripcion(e.target.value);
  };

  const handleRequisitosChange = (e) => {
    setRequisitos(e.target.value);
  };

  const handlePrivacidadChange = (e) => {
    setPrivacidad(e.target.value);
  };

  const handleHoraEventoChange = (e) => {
    setHoraEvento(e.target.value);
  };

  const handleImagenChange = (e) => {
    const file = e.target.files[0];
    setImagen(file);
  };

  const handleIdPersonaChange = (e) => {
    setIdPersona(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('tituloEvento', tituloEvento);
    formData.append('fechaInicioEvento', fechaInicio);
    formData.append('fechaFinEvento', fechaFin);
    //formData.append('ubicacion', ubicacion);
    formData.append('descripcionEvento', descripcion);
    formData.append('aficheEvento',imagen);
    formData.append('estadoEvento', estado);
    formData.append('horaInicioEvento', horaEvento);
    //formData.append('imagen', imagen);
    formData.append('requisitosEvento', requisitos);
    formData.append('idAdministrador', idAdministrador);

    try {
      await axios.post('http://localhost:8000/api/evento', formData);
      console.log('Evento creado con éxito');
    } catch (error) {
      console.error('Error al crear evento', error);
    }
  };


  return (
    <form onSubmit={handleSubmit} className="tweet-composer">
      <div className="composer-form">
        <label htmlFor="titulo">Titulo del Evento</label>
        <input
          type="text"
          id="titulo"
          name="titulo"
          placeholder="Título"
          value={titulo}
          onChange={handleTituloChange}
          required
        />

        <div className="Fecha">
          <div className="FechaInicio">
            <label htmlFor="fecha-inicio">Fecha Inicio</label>
            <input
              className="FechaDesing"
              type="date"
              id="fecha-inicio"
              name="fecha-inicio"
              value={fechaInicio}
              onChange={handleFechaInicioChange}
              required
            />
          </div>

          <div className="FechaFinal">
            <label htmlFor="fecha-fin">Fecha Fin</label>
            <input
              className="FechaDesing"
              type="date"
              id="fecha-fin"
              name="fecha-fin"
              value={fechaFin}
              onChange={handleFechaFinChange}
              required
            />
          </div>
        </div>

        <label htmlFor="ubicacion">Ubicación</label>
        <input
          type="text"
          id="ubicacion"
          name="ubicacion"
          placeholder="Ubicación"
          value={ubicacion}
          onChange={handleUbicacionChange}
        />

        <label htmlFor="descripcion">Descripción</label>
        <textarea
          id="descripcion"
          name="descripcion"
          placeholder="Descripción"
          rows="4"
          value={descripcion}
          onChange={handleDescripcionChange}
          required
        ></textarea>

        <label htmlFor="requisitos">Requisitos del evento</label>
        <textarea
          id="requisitos"
          name="requisitos"
          placeholder="Requisitos"
          rows="4"
          value={requisitos}
          onChange={handleRequisitosChange}
          required
        ></textarea>

        <div className="img1">
          <div className="EstadoEvento">
            <label htmlFor="estado">Tipo de evento</label>
            <select
              id="estado"
              name="estado"
              value={estado}
              onChange={handlePrivacidadChange}
            >
              <option value="true">true</option>
              <option value="false">flase</option>
            </select>
          </div>

          <div className="horaEvento">
            <label htmlFor="horaEvento">Hora</label>
            <input
              type="time"
              id="horaEvento"
              name="horaEvento"
              value={horaEvento}
              onChange={handleHoraEventoChange}
              required
            />
          </div>
        </div>

        <div>
          <input
            type="file"
            id="icono-tipo"
            name="icono-tipo"
            accept="image/*"
            onChange={handleImagenChange}
          />
          {imagen && <img id="imagen-preview" src={URL.createObjectURL(imagen)} alt="Vista previa de la imagen" />}
        </div>

        <div className="container">
          <div className="other-elements"></div>
          <div>
            <button className="tweet-button" type="submit">
              Cancelar
            </button>
            <button className="tweet-button" type="submit">
              Guardar
            </button>

          </div>
        </div>
      </div>
    </form>
  );
};

export default EventForm;
