import React, { useState } from 'react';
import axios from 'axios';
import './assets/configurarEvento.css';

const ConfiguracionEvento = () => {
    const [selectedOptions, setSelectedOptions] = useState([]);
  
    const handleOptionClick = (option) => {
      if (selectedOptions.includes(option)) {
        setSelectedOptions(selectedOptions.filter((item) => item !== option));
      } else {
        setSelectedOptions([...selectedOptions, option]);
      }
    };
  
    return (
      <div className="configuracion-evento">
        <h2>Título del Formulario</h2>
        <div className="opciones-container">
          <div className="columna">
            {Array.from({ length: 4 }).map((_, index) => (
              <label key={index}>
                <input
                  type="checkbox"
                  checked={selectedOptions.includes(`Opción ${index + 1}`)}
                  onChange={() => handleOptionClick(`Opción ${index + 1}`)}
                /> Opción {index + 1}
              </label>
            ))}
          </div>
          <div className="columna">
            {Array.from({ length: 4 }).map((_, index) => (
              <label key={index + 4}>
                <input
                  type="checkbox"
                  checked={selectedOptions.includes(`Opción ${index + 5}`)}
                  onChange={() => handleOptionClick(`Opción ${index + 5}`)}
                /> Opción {index + 5}
              </label>
            ))}
          </div>
        </div>
      </div>
    );
  };
  
  export default ConfiguracionEvento;
