import React, { useState, useEffect } from 'react';
import './assets/HomeVistaEvento.css';
import axios from '../components/api/conexionApi';
import useFetchActividades from '../components/utils/ObtenerActividadCronograma'

const nombreCronograma="Cronograma general";
const HomeVistaEvento = () => {


  const [eventos, setEventos] = useState([]);

  useEffect(() => {
    const obtenerEventos = async () => {
      try {
        const response = await axios.get('./eventoConActividad');
        const data = response.data;
        setEventos(data.eventosConProgramaGeneral); // Ajusta según la estructura de tu respuesta
      } catch (error) {
        console.error('Error al obtener eventos:', error);
      }
    };

    obtenerEventos();
  }, []);

  const handleRegistrarseClick = async (idEvento) => {
    window.location.href = `./VerInformacionEvento?id=${idEvento}`;
  };

  return (
    <div className="cuerpo">
      <h3 className="titulo_home">Eventos en Curso</h3>
      <hr />

      <div className="eventos-container">
        {eventos.map(eventosConProgramaGeneral => (
          <div key={eventosConProgramaGeneral.evento.idEvento} className="evento-rectangulo">
            <img src={eventosConProgramaGeneral.evento.aficheEvento} alt={`Evento ${evento.idEvento}`} />
            
            <div className="evento-info">
              <h4>{eventosConProgramaGeneral.evento.tituloEvento}</h4>
              
              <p>Fecha de inicio: {evento.actividades.fechaInicioActividad}</p>
              <p>Fecha de fin: {evento.actividades.fechaFinActividad}</p>
              
              {evento.evento.descripcionEvento && <p>Descripción: {evento.evento.descripcionEvento}</p>}
              <p>Estado: {evento.estadoEvento === 1 ? 'Activo' : 'Inactivo'}</p>
              <button className="masinfo" onClick={() => handleRegistrarseClick(evento.idEvento)}>
                Más información
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomeVistaEvento;
