import React, { useState, useEffect } from 'react';
import './assets/HomeVistaEvento.css'
import axios from 'axios';
import Afiche from'./imgs/competencia.jpg'
import botonIzquierda from'./imgs/flechaIzquierda.png'
import botonDerecha from'./imgs/correcta.png'


const HomeVistaEvento = () => {

  const handleRegistrarseClick = async() => {
    const response = await axios.get('http://localhost:8000/api/obtenerUltimoIdEvento');
    const ultimoId = response.data.ultimoId;
    window.location.href = `./VerInformacionEvento?id=${ultimoId}`;
}

  

  const [imagenUrl, setImagenUrl] = useState('4');

  const imagenes = [Afiche1, Afiche2, Afiche3];
  const [imagenActual, setImagenActual] = useState(0);

  const irAImagenAnterior = () => {
    setImagenActual((prev) => (prev === 0 ? imagenes.length - 1 : prev - 1));
  };

  const irAImagenSiguiente = () => {
    setImagenActual((prev) => (prev === imagenes.length - 1 ? 0 : prev + 1));
  };





  
  return (
    <div className="body">
      <h3 className="titulo_home">Título evento</h3>  
      <hr />
        
        
        
        
        
        
    <div className="eventos">
      <div className='contImagen'>
        <img src='' className="imagen_evento" />
      </div>

      <div className='contFecha'>
        <a href="">
          <img className="eventoFlecha" src={FlechaIzquierda} alt="Flecha Izquierda" />
        </a>
      </div>

      <div className='contImagen'>
        <img src={imagenes[imagenActual]} alt={`Imagen ${imagenActual + 1}`} className="imagen_evento" />
      </div>
      
      <div className='contFecha'>
        <a href="">  
          <img className="eventoFlecha1" src={FlechaDerecha} alt="Flecha Derecha" />
        </a>
      </div>

      <div className='contImagen'>
        <img src='' className="imagen_evento" />
      </div>
    </div>






        <div className='boton_mas_informacion'>
          <button className="masinfo" type="submit" onClick={handleRegistrarseClick} >
              Más información
          </button>
        </div>


        <div className="sobre_icpc">
          <hr />
          <h3>Competición internacional universitaria de programación ICPC</h3>
          <div className="descripcion_sobre_icpc">
            <p>
              La ICPC se inicia en 1970, como un programa de innovación para incrementar la aptitud de resolución de problemas, y la oportunidad para estudiantes fuertes en el campo de la computación. Con el tiempo, el concurso se convirtió en una competencia de varios niveles con el primer campeonato realizado en 1977; desde entonces el concurso se ha expandido a una colaboración mundial de universidades que albergan competencias regionales que hacen avanzar a los equipos a la ronda final anual del campeonato mundial, el ICPC World Finals.
            </p>
          </div>
        </div>

    </div>
  );
};

export default HomeVistaEvento;
