import React, { useState } from 'react';
import axios from 'axios';
import './formulario.css';

const EventForm = () => {
  const [titulo, setTitulo] = useState('');
  const [fechaHora, setFechaHora] = useState('');
  const [ubicacion, setUbicacion] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [privacidad, setPrivacidad] = useState('publico');
  const [imagen, setImagen] = useState(null);

  const handleTituloChange = (e) => {
    setTitulo(e.target.value);
  };

  const handleFechaHoraChange = (e) => {
    setFechaHora(e.target.value);
  };

  const handleUbicacionChange = (e) => {
    setUbicacion(e.target.value);
  };

  const handleDescripcionChange = (e) => {
    setDescripcion(e.target.value);
  };

  const handlePrivacidadChange = (e) => {
    setPrivacidad(e.target.value);
  };

  const handleImagenChange = (e) => {
    // Puedes manejar la carga de la imagen aquí
    const file = e.target.files[0];
    setImagen(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('titulo', titulo);
    formData.append('fechaHora', fechaHora);
    formData.append('ubicacion', ubicacion);
    formData.append('descripcion', descripcion);
    formData.append('privacidad', privacidad);
    if (imagen) {
      formData.append('imagen', imagen);
    }

    try {
      // Realizar la solicitud POST con formData usando axios
      await axios.post('http://localhost:8000/api/eventos', formData);

      console.log('Evento creado con éxito');
    } catch (error) {
      console.error('Error al crear evento', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="tweet-composer">
      <div className="composer-form">
        <label htmlFor="Titulo">Titulo del Evento</label>
        <input
          type="text"
          id="titulo"
          name="titulo"
          placeholder="Título"
          value={titulo}
          onChange={handleTituloChange}
          required
        />


        <div className="Fecha" >

          <div>
          <label htmlFor="FechaInicio">Fecha Inicio</label>
          <input
            className="FechaInicio"
            type="date"
            id="fecha-inicio"
            name="fecha-hora"
            placeholder="FechaInicio"
            value={fechaHora}
            onChange={handleFechaHoraChange}
            required
          />
          </div>

          <div className="FechaFinal"> 
            <label htmlFor="FechaFinal">Fecha Fin</label>
          <input
              type="date"
              id="fecha-fin"
              name="fecha-hora"
              placeholder="FechaFinal"
              value={fechaHora}
              onChange={handleFechaHoraChange}
              required
            />
          </div>
        </div>



        <label htmlFor="Ubicacion">Ubicación</label>
        <input
          type="text"
          id="ubicacion"
          name="ubicacion"
          placeholder="Ubicación"
          value={ubicacion}
          onChange={handleUbicacionChange}
        />


        <label htmlFor="Descripcion">Descripción</label>
        <textarea
          id="descripcion"
          name="descripcion"
          placeholder="Descripción"
          rows="4"
          value={descripcion}
          onChange={handleDescripcionChange}
          required
        ></textarea>
        
        <label htmlFor="Requisitos">Requisitos del evento</label>
        <textarea
          id="Requisitos"
          name="Requisitos"
          placeholder="Requisitos"
          rows="4"
          value={descripcion}
          onChange={handleDescripcionChange}
          required
        ></textarea>

        
        <div className='img'>
          <div >
            <label htmlFor="TipoEvento">Tipo de evento</label>
            <select id="TipoEvento" name="TipoEvento" value={privacidad} onChange={handlePrivacidadChange}>
            <option value="Competición">Competición</option>
            <option value="Taller">Taller</option>
            </select>
        </div>
          <div>
            <label htmlFor="HoraEvento">Hora</label>
            <input
                type="Time"
                id="HoraEvento"
                name="HoraEvento"
                placeholder="HoraEvento"
                value={fechaHora}
                onChange={handleFechaHoraChange}
                required
              />
          </div>
        </div>


        <div>
          <input
            type="file"
            id="icono-tipo"
            name="icono-tipo"
            accept="image/*"
            onChange={handleImagenChange}
          />
          {imagen && <img id="imagen-preview" src={URL.createObjectURL(imagen)} alt="Vista previa de la imagen" />}
        </div>
        
        <button className="tweet-button" type="submit">
          Crear Evento
        </button>
        
      </div>
    </form>
  );
};

export default EventForm;
