import React, { useState, useEffect } from 'react';
import axios from './api/conexionApi';
import './assets/VerInformacionEvento.css'
import ModalParaRegistro from './ModalSeleccionarRegistro';
import './assets/Titulo.css'
import 'react-calendar/dist/Calendar.css'; // Importar estilos CSS
import CalendarioDesplegable from './CalendarioDesplegable';
import RegistrarEquipo from '';
const EventoId=1;
const varEquipo = 'Grupal';
//const varEquipo = 'Individual';


const EventoComponent = () => {
//
const [mostrarRegistrarEquipo, setMostrarRegistrarEquipo] = useState(false);
const [eventoIdSeleccionado, setEventoIdSeleccionado] = useState(null);

// Función para manejar el cierre del modal
const handleClose = (eventoId) => {
  // Guarda el eventoId seleccionado
  setEventoIdSeleccionado(eventoId);
  // Muestra el componente RegistrarEquipo
  setMostrarRegistrarEquipo(true);
}
//------------------
//modal de seleccion
const [showSeleccionModal, setShowSeleccionModal] = useState(false);

  const closeSeleccionModal = () => {
    setShowSeleccionModal(false);
  };

  const handleModalSeleccion = () => {
    if(varEquipo==="Grupal"){
        setShowSeleccionModal(true);
    }else{

        window.location.href = `./registroParticipantes?${EventoId}`;
    }
    
  };
//--------------------------------------------------------------------------


    //tabla evento------------------------------------------------------------
   
    const [evento, setEvento] = useState(null);
    useEffect(() => {
        axios.get(`eventos/${EventoId}`) 
            .then(response => {
                setEvento(response.data.evento);
            })
            .catch(error => {
                console.error('Error al obtener el evento:', error);
            });
    }, []);

    //tabla regla
            const [reglas, setReglas] = useState([]);
            useEffect(() => {
            axios.get(`obtenerReglasDeEvento/${EventoId}`)
            .then(response => {
                setReglas(response.data.reglas);
            })
            .catch(error => {
                console.error('Error al obtener las reglas:', error);
            });
            }, []);

        //tabla requisito
        const [requisitos, setRequisitos] = useState([]);
            useEffect(() => {
            axios.get(`obtenerRequisitosDeEvento/${EventoId}`)
            .then(response => {
                setRequisitos(response.data.requisitos);
            })
            .catch(error => {
                console.error('Error al obtener los requisitos:', error);
            });
        }, []);

           

             
              
             
              
    if (!evento ) {
        return <div>Cargando evento...</div>;
    };
    

    return (
        <div className="all-info-container">
            
                <h1 className='titulo-1'>{evento.tituloEvento}</h1>
          
            <br />

        
            <div className='date-container'>
            
                    <div className='date-box'>
                    <strong>Estado:</strong>
                    <p className={`info-box ${evento.estadoEvento ? 'estado-activo' : 'estado-inactivo'}`}><strong>{evento.estadoEvento ? 'Activo' : 'Inactivo'}</strong></p>
                    </div>
            </div>
                    <div className='date-container'>
                    <div className='date-box'> 
                    <br />
                    
                    
                    <CalendarioDesplegable EventoId={`${EventoId}`}  />
                    </div>
                    </div>
                 
                    
            
        
            <br />
            

            
            <div className='date-box'>
                <strong>Dinamica:</strong>  
                <p className='info-box'> {evento.participacion}</p>
        <br />

                {evento.participacion === varEquipo  && (

                <div className='date-container'>
                    <>  

                    <div className='date-box'>
                        <strong>Cantidad de entrenadores:</strong>
                        <p className='info-box'> {evento.numEntrenadores}</p>
                    </div>
                    
                    <div className='date-box'>
                        <strong>Cantidad de participantes:</strong>
                        <p className='info-box'> {evento.numParticipantes}</p>
                    </div>
                </>
                </div>
            )}

             {evento.participacion === 'Individual' && (
        
            <div >
            
            </div>
                )}
            </div>



        <br />

            <div  className='info-box'>
                                       
                        <strong>Reglas</strong>
                <ol>
                    {reglas.map(reglas => (
                        
                    <li>
                        <strong> {reglas.nombreRegla}</strong> 
                        <p></p>
                        <strong>Descripción:</strong> 
                        <p>{reglas.descripcionRegla}</p>
                    </li>
                    ))}
                </ol>
                </div>

                               <div  className='info-box'>
                  
                                
                <strong>Requisitos</strong>
                <ol>
                    {requisitos.map(requisito => (
                    <li>
                        
                        <strong>{requisito.nombreRequisito}</strong> 
                        <p></p>
                        <strong>Descripción:</strong> 
                        <p>{requisito.descripcionRequisito}</p>
                        
                    </li>
                    ))}
                </ol>

                    
            </div>
                                 

            <div className="button-container-RI">  

            <button className="blue-button-RI" onClick={() => window.history.back()}>Volver Atrás</button>         
                <button className="blue-button-RI" onClick={() => handleModalSeleccion()}>Registrarse</button>
                
            </div>

       
            <ModalParaRegistro EventoId={eventoIdSeleccionado} onClose={handleClose} />

            

            {mostrarRegistrarEquipo && <RegistrarEquipo EventoId={eventoIdSeleccionado} />}


        </div>
        
    );
    
}



export default EventoComponent;
