import React, { useState } from 'react';

import './assets/ModalErrorR.css';

const ModalSalir = ({ message, onClose }) => {
  const [abandonarRegistro, setAbandonarRegistro] = useState(false);
  
  const handleSiClick = () => {
    // Cerrar el modal y abandonar el registro
    setAbandonarRegistro(true);
    onClose();
  };

  const handleNoClick = () => {
    // Cerrar solo el modal, no abandonar el registro
    onClose();
  };

  const handleModalClose = () => {
    // Redirigir a la página de inicio solo si se hace clic en "No"
    if (!abandonarRegistro) {
        window.location.href = '../pages/Home/';
    }
  };

  return (
    <div className='modal-container' onClick={handleModalClose}>
      <div className='modal-content error' onClick={(e) => e.stopPropagation()}>
        <div className='message'>{message}</div>
        <div className='btn-options'>
          <button onClick={handleSiClick}>Sí</button>
          <button onClick={handleNoClick}>No</button>
        </div>
      </div>
    </div>
  );
};

export default ModalSalir;
