import React, { useState } from 'react';
import './assets/ModalRegistroParticipantes.css'

const Modal = ({ isOpen, closeModal, datos, enviarCodigo, verificarCodigo, codigoEnviado }) => {
  const [codigoIngresado, setCodigoIngresado] = useState('');

  if (!isOpen) {
    return null;
  }

  const handleYes = async () => {
    await enviarCodigo(datos.correo);
  };

  const handleVerify = () => {
    verificarCodigo(codigoIngresado);
  }

  return (
    <div className='modal-container'>
      <div className='modal-content'>
        <h2>¿Ud. ya se ha registrado a un evento ICPC antes?</h2>
        <strong> Este es su correo: {datos.correo}</strong>

        {codigoEnviado ? (
          <>
            <input 
              type="text" 
              value={codigoIngresado} 
              onChange={(e) => setCodigoIngresado(e.target.value)} 
              placeholder="Ingrese el código enviado a su correo" 
            />
            <button className='boton-ok' onClick={handleVerify}>Verificar Código</button>
          </>
        ) : (
          <>
            <button className='boton-ok' onClick={handleYes}>Sí</button>
            <button className='boton-ok' onClick={closeModal}>No</button>
          </>
        )}

        <span className="cerrar" onClick={closeModal}>&times;</span>
      </div>
    </div>
  );
};

export default Modal;
