import React, { useState, useEffect } from 'react';
import axios from './api/conexionApi';
const nombreActividadBuscada="Cronograma general";

const EditarEvento = (eventoId) => {
  const [eventoEditado, setEventoEditado] = useState({
    tituloEvento: '',
    participacion: '',
    numParticipantes: 0,
    numEntrenadores: 0,
    descripcionEvento: '',
    estadoEvento: '',
    aficheEvento: '',
    idTipoEvento: 0,
    idAdministrador: 0,
  });
  const [ActividadCronograma, setActividadCronograma] = useState({
    nombreActividad:"",
    descripcionActividad:"",
    modalidad:"",
    fechaInicioActividad:"",
    fechaFinActividad:"",
    horaInicioActividad:"",
    ubicacionActividad:"",
  })
  const [ActividadEditado, setActividadEditado] = useState({
    nombreActividad:"",
    descripcionActividad:"",
    modalidad:"",
    fechaInicioActividad:"",
    fechaFinActividad:"",
    horaInicioActividad:"",
    ubicacionActividad:"",
  });
  const [Requisitos, setRequisitos] = useState({
    nombreRequisito:"",
    descripcionRequisito:""
      
  });
  
  const [Reglas, setReglas] = useState({
    nombreRegla:"",
    descripcionRegla:""
  });

  useEffect(() => {
    console.log("el id es: ",eventoId.eventoId)
    axios.get(`eventos/${eventoId.eventoId}`) 
      .then(response => {
        setEventoEditado(response.data.evento);
        console.log("el eventoEditado es: ",response.data.evento)

        axios.get(`obtenerActividadesDeEvento/${eventoId.eventoId}`) 
        .then(response => {
          setActividadEditado(response.data.actividades);
          console.log("la actividad es: ",response.data.evento)
          axios.get(`obtenerRequisitosDeEvento/${eventoId.eventoId}`) 
          .then(response => {
            setRequisitos(response.data.requisitos);
            console.log("requisitos es: ",response.data.evento)

            axios.get(`obtenerReglasDeEvento/${eventoId.eventoId}`) 
      .then(response => {
        setReglas(response.data.reglas);
        console.log("reglas es: ",response.data.evento)

        axios.get(`buscarActividadPorNombre/${eventoId.eventoId}/${encodeURIComponent(nombreActividadBuscada)}`)
        .then(response => {
          setActividadCronograma(response.data.actividadPorNombre[0]);
          console.log("reglas es: ",response.data.evento)
        })
        .catch(error => {
          console.error('Error al obtener la información del reglas', error);
        });

      })
      .catch(error => {
        console.error('Error al obtener la información del reglas', error);
      });
          })
          .catch(error => {
            console.error('Error al obtener la información del requisitos', error);
          });
        })
        .catch(error => {
          console.error('Error al obtener la información del actividad', error);
        });

      })
      .catch(error => {
        console.error('Error al obtener la información del eventoEditado', error);
      });
  });

  const handleInputChange = e => {
    const { name, value } = e.target;
    setEventoEditado({
      ...eventoEditado,
      [name]: value,
    });
  };

  const handleSubmit = e => {
    e.preventDefault();
    
    // Hacer una solicitud para actualizar la información del eventoEditado
    axios.put(`./eventoActualizar/${eventoId.eventoId}`, eventoEditado)
      .then(response => {
        console.log('Evento actualizado con éxito', response.data.evento);
        // Puedes redirigir al usuario a la página del eventoEditado o realizar otras acciones después de la actualización
      })
      .catch(error => {
        console.error('Error al actualizar el eventoEditado', error);
      });
  };

  return (
    <div>
      <h2>Editar Evento</h2>
      <form onSubmit={handleSubmit}>
        <label>Título del Evento:
          <input type="text" 
          name="tituloEvento" 
          value={eventoEditado.tituloEvento} 
          onChange={handleInputChange} />
        </label>

        <label>Hora Inicio:
          <input type="text" 
          name="horaInicioActividad" 
          value={ActividadCronograma.horaInicioActividad} 
          onChange={handleInputChange} />
        </label>

        <label>Fecha Inicio:
          <input type="text" 
          name="fechaInicioActividad" 
          value={ActividadCronograma.fechaInicioActividad} 
          onChange={handleInputChange} />
        </label>

        <label>Fecha Fin:
          <input type="text" 
          name="fechaFinActividad" 
          value={ActividadCronograma.fechaFinActividad} 
          onChange={handleInputChange} />
        </label>


        <button type="submit">Guardar Cambios</button>
      </form>
    </div>
  );
};

export default EditarEvento;
