import React, { useState } from 'react';
import axios from 'axios';
import '../components/assets/FormCrearEvento.css';
import Alert from'./Alert';
const EventForm = () => {


  const [tituloEvento, setTituloEvento] = useState('');
  const [fechaInicio, setFechaInicio] = useState('null');
  const [fechaFin, setFechaFin] = useState('null');
  const [modalidad, setModalidad] = useState('Persencial');
  const [participacion, setParticipacion] = useState('');
  const [numParticipantes, setNumParticipantes] = useState('');
  const [numEntrenadores, setNumEntrenadores] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [ubicacion, setUbicacion] = useState("");
  const [estado, setEstado] = useState(true);
  const [horaEvento, setHoraEvento] = useState('00:00');
  const [imagen, setImagen] = useState(null);
  const [reglas, setReglas] = useState('');
  const [idTipoEvento, setIdTipoEvento] = useState('1');
  const [idAdministrador, setIdAdministrador] = useState('1');
  const [actividad, setActividad] = useState('');
  const [requisitos, setRequisitos] = useState([]);
  const [nuevoRequisito, setNuevoRequisito] = useState('');

  const [modalVisible, setModalVisible] = useState(false);
  const [tituloEventoError, setTituloEventoError] = useState(false);
  const [numParticipantesError, setnumParticipantesError] = useState(false);
  const [fechaInicioError, setFechaInicioError] = useState(false);
  const [fechaFinError, setFechaFinError] = useState(false);
  const [participacionError, setParticipacionError] = useState(false);
  const [numEntrenadoresError, setNumEntrenadoresError] = useState(false);
  const [descripcionError, setDescripcionError] = useState(false);
  const [ubicacionError, setUbicacionError] = useState(false);
  const [estadoError, setEstadoError] = useState(false);
  const [horaEventoError, setHoraEventoError] = useState(false);
  const [imagenError, setImagenError] = useState(false);
  const [reglasError, setReglasError] = useState(false);
  const [idTipoEventoError, setIdTipoEventoError] = useState(false);
  const [idAdministradorError, setIdAdministradorError] = useState(false);
  const [actividadError, setActividadError] = useState(false);
  

  const agregarRequisito = () => {
    if (nuevoRequisito) {
      setRequisitos([...requisitos, nuevoRequisito]);
      setNuevoRequisito(''); // Limpiar el campo después de agregar un requisito
    }
  };

  const eliminarRequisito = (index) => {
    const nuevosRequisitos = [...requisitos];
    nuevosRequisitos.splice(index, 1);
    setRequisitos(nuevosRequisitos);
  };

  const handleImagenChange = (e) => {
    const file = e.target.files[0];
    // Puedes realizar operaciones con el archivo, si es necesario
    setImagen(file);
  };
  const handleSubmit = async (e) => {
    e.preventDefault();

    


    const handleRequisitoChange = (e, index) => {
      const newRequisitos = [...requisitos];
      newRequisitos[index] = e.target.value;
      setRequisitos(newRequisitos);
    };
    
    const handleAddRequisito = () => {
      setRequisitos([...requisitos, ""]);
    };
    
    const handleRemoveRequisito = (index) => {
      const newRequisitos = [...requisitos];
      newRequisitos.splice(index, 1);
      setRequisitos(newRequisitos);
    };





    try {
      await axios.post('http://localhost:8000/api/evento', {
        tituloEvento: tituloEvento,
        fechaInicioEvento: fechaInicio,
        fechaFinEvento: fechaFin,
        modalidad: modalidad,
        participacion: participacion,
        numParticipantes: numParticipantes,
        numEntrenadores: numEntrenadores,
        descripcionEvento: descripcion,
        requisitosEvento: requisitos,
        ubicacion: ubicacion,
        estadoEvento: estado ? 1 : 0,
        horaInicioEvento: horaEvento,
        aficheEvento: imagen,
        reglas: reglas,
        idTipoEvento:idTipoEvento,
        idAdministrador: idAdministrador
      });
      console.log('Evento creado con éxito');
      setModalVisible(true);
    } catch (error) {
   
      if (error.response) {
        
        console.error('Respuesta del servidor con error:', error.response);
        console.error('Código de estado HTTP:', error.response.status);
      } else if (error.request) {
        console.error('No se recibió respuesta del servidor:', error.request);
      } else {
        console.error('Error durante la configuración de la solicitud:', error.message);
      }
      setModalVisible(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="tweet-composer">
      <h1 className="CrearEvento">Crear Evento</h1>

    <div className="PrimeraFila">
      <div className="TituloEvento">
        <div className='Campovacio'>
          <label htmlFor="tituloEvento">Nombre del evento:</label>
          <div className="ColorCampoVacio">*</div>
        </div>
        <input
          type="text"
          id="tituloEvento"
          name="tituloEvento"
          placeholder="Nombre"
          value={tituloEvento}
          onChange={(e) => setTituloEvento(e.target.value)}
          onBlur={() => setTituloEventoError(tituloEvento.trim() === "")}
          className={tituloEventoError ? "campo-vacio" : ""}
        />
      </div>

      <div className="horaEvento">
        <div className="Campovacio">
          <label htmlFor="horaEvento">Hora de inicio</label>
          <div className="ColorCampoVacio">*</div>
        </div>
        <input
          type="time"
          id="horaEvento"
          name="horaEvento"
          value={horaEvento}
          onChange={(e) => setHoraEvento(e.target.value)}
          onBlur={() => setHoraEventoError(horaEvento.trim() === "")}
          className={horaEventoError ? "campo-vacio" : ""}
          required
        />
      </div>
    </div>

    <div className="Fecha">
        <div className="FechaInicio">
          <div className="Campovacio">
            <label htmlFor="fecha-inicio">Fecha Inicio</label>
            <div className="ColorCampoVacio">*</div>
          </div>
          <input
            className={`FechaDesing ${fechaInicioError ? "campo-vacio" : "null"}`}
            type="date"
            id="fecha-inicio"
            name="fecha-inicio"
            value={fechaInicio}
            onChange={(e) => setFechaInicio(e.target.value)}
            onBlur={() => setFechaInicioError(fechaInicio.trim() === "null")}
            required
          />
        </div>
        <div className="FechaFinal">
          <div className="Campovacio">
            <label htmlFor="fecha-fin">Fecha Fin  </label>
            <div className="ColorCampoVacio">*</div>
          </div>         
          <input
            className={`FechaDesing ${fechaFinError ? "campo-vacio" : "null"}`}
            type="date"
            id="fecha-fin"
            name="fecha-fin"
            value={fechaFin}
            onChange={(e) => setFechaFin(e.target.value)}
            onBlur={() => setFechaFinError(fechaFin.trim() === "null")}
            required
          />
        </div>
      </div>

      <div>




      <div className="composer-form">
        <div className="errorInf">
          <div className='Campovacio'>
            <label htmlFor="idTipoEvento">Tipo de evento</label>
            <p>*</p>
          </div>
            {(tituloEventoError || 
            fechaFinError || 
            fechaInicioError || 
            participacionError|| 
            numEntrenadoresError || 
            numParticipantesError || 
            descripcionError || 
            reglasError || 
            horaEventoError || 
            ubicacionError
            )&& <Alert/>}
       </div>
      <select
        id="idTipoEvento"
        name="idTipoEvento"
        value={idTipoEvento}
        onChange={(e) => setIdTipoEvento(e.target.value)}
        >
        <option value="seleccione">-- seleccione --</option>
        <option value="Competencia">Competencia</option>
        <option value="Taller">Taller</option>
        <option value="Evento solidario">Evento solidario</option>
      </select>

      </div>



 

      
      
      <div className="ConjuntoA">
        
          

        <div className="Modalidad">
          <label htmlFor="modalidad">Modalidad</label>
          <select
            id="modalidad"
            name="modalidad"
            value={modalidad}
            onChange={(e) => setModalidad(e.target.value)}
            required
          >
            <option value="seleccione">-- seleccione --</option>
            <option value="Virtual">Virtual</option>
            <option value="Presencial">Presencial</option>
          </select>
        </div>
      </div>          

      <div className="errorInf">
        <label htmlFor="ubicacion">Ubicación</label>
      </div>
      <input
        type="text"
        id="ubicacion"
        name="ubicacion"
        placeholder="Ubicación"
        value={ubicacion}
        onChange={(e) => setUbicacion(e.target.value)}
        onBlur={() => setUbicacionError(ubicacion.trim() === "")}
        className={ubicacionError ? "campo-vacio" : ""}
        required
      />
      
      <label htmlFor="descripcion">Descripción</label>
      <textarea
        id="descripcion"
        n
        ame="descripcion"
        placeholder="Descripción"
        rows="4"
        value={descripcion}
        onChange={(e) => setDescripcion(e.target.value)}
        required
      ></textarea>
     
     <input
        type="file"
        id="image"
        name="image"
        accept="image/*"
        onChange={handleImagenChange}
        style={{ display: 'none' }} // Ocultar el campo de carga de imagen
      />
      {/* Área estilizada para cargar imagen */}
      <label htmlFor="image" className="upload-container">
        <div className="upload-icon"><img src="https://cdn-icons-png.flaticon.com/512/883/883787.png " /></div>
        <div className="upload-text">Haz clic aquí para subir una Imagen</div>
        {/* Vista previa de la imagen */}
        {imagen && (
          <img
            id="image-preview"
            src={URL.createObjectURL(imagen)}
            alt="Vista previa"
          />
        )}
      </label>


      
      <label htmlFor="requisitos">Requisitos de la Actividad</label>
      <div className="Requisitos">
        <div className="distribucionReque">
            {requisitos.length > 0 &&
              requisitos.map((requisito, index) => (
                <div key={index} className="Requisitos">
                  <input
                    type="text"
                    placeholder="Requisitos del evento"
                    value={requisito}
                    onChange={(e) => {
                      const nuevosRequisitos = [...requisitos];
                      nuevosRequisitos[index] = e.target.value;
                      setRequisitos(nuevosRequisitos);
                    }}
                    required
                  />
                  
                  <button type="button" onClick={() => eliminarRequisito(index)} className="BotRequisitos">
                    <img src="https://cdn-icons-png.flaticon.com/512/6722/6722986.png" alt="" className="iconoEliminar"/>
                  </button>
                </div>
                
              ))}
          <input
            type="text"
            id="requisitos"
            name="requisitos"
            placeholder="Nuevo requisito"
            value={nuevoRequisito}
            onChange={(e) => setNuevoRequisito(e.target.value)}
            required
          />
        </div>
        <button type="button" onClick={agregarRequisito} className="BotRequisitos">
          +
        </button>
      </div>
      












      


    

      <div className="Participantes">
        <div>
          <label>Participación</label>
          <div>
            <label>
              <input
                type="radio"
                name="participacion"
                value="Individual"
                checked={participacion === "Individual"}
                onChange={(e) => setParticipacion(e.target.value)}
                onBlur={() => setParticipacionError(participacion.trim() === "")}
                className={participacionError ? "campo-vacio" : ""}
              />
              Individual
            </label>
          </div>
          <div>
            <label>
              <input
                type="radio"
                name="participacion"
                value="Equipo"
                checked={participacion === "Equipo"}
                onChange={(e) => setParticipacion(e.target.value)}
                onBlur={() => setParticipacionError(participacion.trim() === "")}
                className={participacionError ? "campo-vacio" : ""}
              />
              Equipo
            </label>
          </div>
          
        </div >
        
        <div>
          <div className="errorInf">
            <label htmlFor="numParticipantes">Número de participantes</label>
            {numParticipantesError && <p>*</p>}
          </div> 
          <input
            type="text"
            id="numParticipantes"
            name="numParticipantes"
            placeholder="Número de participantes"
            value={numParticipantes}
            onChange={(e) => setNumParticipantes(e.target.value)}
            onBlur={() => setnumParticipantesError(numParticipantes.trim() === "")}
            className={numParticipantesError ? "campo-vacio" : ""}
            required
          />
        </div>

        <div>

          <div className="errorInf">
          <label htmlFor="numEntrenadores">Número de entrenadores</label>
          {numEntrenadoresError && <p>*</p>}
          </div> 
          <input
            type="text"
            id="numEntrenadores"
            name="numEntrenadores"
            placeholder="Número de entrenadores"
            value={numEntrenadores}
            onChange={(e) => setNumEntrenadores(e.target.value)}
            onBlur={() => setNumEntrenadoresError(numEntrenadores.trim() === "")}
            className={numEntrenadoresError ? "campo-vacio" : ""}
            required
          />
        </div>
        
      </div>

      



      <div>
            <fieldset className="role-fieldset">
              <legend>Actividades</legend>
              <div className="radio-group">
                <div>
                  <div className="errorInf">
                  <label htmlFor="Actividad">Nombre de la actividad</label>
                    {ubicacionError && <p>*</p>}
                  </div>
                  <input
                    type="text"
                    id="actividad"
                    name="actividad"
                    placeholder="actividad"
                    value={actividad}
                    onChange={(e) => setActividad(e.target.value)}
                    onBlur={() => setActividadError(actividad.trim() === "")}
                    className={actividadError ? "campo-vacio" : ""}
                    required
                  />
                </div>

                
              <div className="Fecha">
                <div className="FechaInicio">
                  <div className="errorInf">
                    <label htmlFor="fecha-inicio">Fecha Inicio</label>
                    {fechaInicioError && <p>*</p>}
                  </div>
                  <input
                    className={`FechaDesing ${fechaInicioError ? "campo-vacio" : "null"}`}
                    type="date"
                    id="fecha-inicio"
                    name="fecha-inicio"
                    value={fechaInicio}
                    onChange={(e) => setFechaInicio(e.target.value)}
                    onBlur={() => setFechaInicioError(fechaInicio.trim() === "null")}
                    required
                  />
                </div>

                <div className="FechaFinal">
                  
                  <div className="errorInf">
                  <label htmlFor="fecha-fin">Fecha Fin  </label>
                    {fechaFinError && <p>*</p>}
                  </div>         
                  <input
                    className={`FechaDesing ${fechaFinError ? "campo-vacio" : "null"}`}
                    type="date"
                    id="fecha-fin"
                    name="fecha-fin"
                    value={fechaFin}
                    onChange={(e) => setFechaFin(e.target.value)}
                    onBlur={() => setFechaFinError(fechaFin.trim() === "null")}
                    required
                  />
                </div>
              </div>


              </div>
            </fieldset>
          </div>










      <div className="horaEvento">
      <div className="errorInf">
      <label htmlFor="horaEvento">Hora Inicio</label>
        {horaEventoError && <p>*</p>}
      </div>

      <input
          type="time"
          id="horaEvento"
          name="horaEvento"
          value={horaEvento}
          onChange={(e) => setHoraEvento(e.target.value)}
          onBlur={() => setHoraEventoError(horaEvento.trim() === "")}
        className={horaEventoError ? "campo-vacio" : ""}
          required
        />
      </div>
      <label htmlFor="requisitos">Requisitos de la Actividad</label>
      <input
        type="text"
        id="requisitos"
        name="requisitos"
        placeholder="Requisitos del evento"
        value={requisitos}
        onChange={(e) => setRequisitos(e.target.value)}
        required
      />
      
      <label htmlFor="reglas">Reglas</label>
        <input
          type="text"
          id="reglas"
          name="reglas"
          placeholder="reglas"
          value={reglas}
          onChange={(e) => setReglas(e.target.value)}
          required
        />

      <div className="errorInf">
      <label htmlFor="ubicacion">Ubicación</label>
        {ubicacionError && <p>*</p>}
      </div>
      
      <input
        type="text"
        id="ubicacion"
        name="ubicacion"
        placeholder="Ubicación"
        value={ubicacion}
        onChange={(e) => setUbicacion(e.target.value)}
        onBlur={() => setUbicacionError(ubicacion.trim() === "")}
        className={ubicacionError ? "campo-vacio" : ""}
        required
      />

    
      


     

      


{modalVisible && (
      <div className="modal">
        <div className="modal-content">
          <span className="close" onClick={() => setModalVisible(false)}>
            
          </span>
          <p>Creado con exito!</p>
          
        </div>
      </div>
    )}

      <div className="container">
        <div className="other-elements"></div>
        <div>
          <button className="tweet-button" type="submit" onClick={() => window.history.back()}>
            Cancelar
          </button>
          <button className="tweet-button" type="submit">
            Guardar
          </button>
        </div>
      </div>
    </div>
  </form>
  );
};

export default EventForm;
