import React, { useState } from 'react';
import './assets/ModalPersonaEncontrada.css';
import enviarCorreo from './utils/EnviarCorreo'; // Ajusta la ruta según sea necesario

const Modal = ({ onClose, handleYes, datos }) => {
  const [codigo, setCodigo] = useState(Array(5).fill(''));
  const [codigoEnviado, setCodigoEnviado] = useState(false);
  const [errorCodigo, setErrorCodigo] = useState('');
  const [codigoIngresado, setCodigoIngresado] = useState('');

  const generarCodigo = () => {
    const nuevoCodigo = Math.floor(Math.random() * 100000).toString().padStart(5, '0');
    return nuevoCodigo;
  };
  
  const enviarCodigo = async () => {
    const nuevoCodigo = generarCodigo();
    setCodigo(nuevoCodigo);

    try {
      await enviarCorreo(datos.correo, 'Tu Código de Verificación', `Tu código es: ${nuevoCodigo}`);
      setCodigoEnviado(true);
    } catch (error) {
      console.error('Error al enviar el correo', error);
    }
  };

  const verificarCodigo = () => {
    if (codigo === codigoIngresado) {
      handleYes();
    } else {
      setErrorCodigo('El código ingresado no es correcto.');
    }
  };
  const handleChange = (e) => {
    const inputValue = e.target.value;
    // Validar que solo se ingresen números y limitar la longitud a 5 caracteres
    const soloNumeros = inputValue.replace(/[^\d]/g, ''); // Elimina caracteres no numéricos
    const codigoValidado = soloNumeros.slice(0, 5); // Limita la longitud a 5 caracteres

    setCodigoIngresado(codigoValidado);
  };

  return (
    <div className='modal-container'>
      <div className='modal-content'>
        <h2>¿Ud. ya se ha registrado a un evento ICPC antes?</h2>
        <strong> Este es su correo: {datos.correo}</strong>
        {!codigoEnviado ? (
          <>
            <button className='boton-ok' onClick={enviarCodigo}>Enviar Código</button>
            <button className='boton-ok' onClick={onClose}>No</button>
          </>
        ) : (
          <>
 <div className="codigo-inputs">
          {codigo.map((caracter, index) => (
            <input
              key={index}
              id={`codigo-${index}`}
              className="codigo-input"
              value={caracter}
              onChange={(e) => handleChange(index, e)}
              maxLength="1"
              type="text"
            />
          ))}
        </div>       
            <button className='boton-ok' onClick={verificarCodigo}>Verificar Código</button>
          </>
        )}
        {errorCodigo && <p className="error">{errorCodigo}</p>}
        <span className="cerrar" onClick={onClose}>&times;</span>
      </div>
    </div>
  );
};

export default Modal;


